export async function getKoyebDeployment({
  deploymentId,
  token,
}: {
  deploymentId: string;
  token: string;
}) {
  const response = await fetch(
    `https://app.koyeb.com/v1/deployments/${deploymentId}`,
    {
      method: 'GET',
