export async function deleteApp(
  request: FastifyRequest,
  reply: FastifyReply,
): Promise<void> {
  const user = request.user;
  const { id } = request.params as { id: string };

  if (!validate(id)) {
    return reply.status(400).send({ error: 'Invalid app ID' });
  }

  try {
    // First, verify the app exists, belongs to the user, and is not already deleted
    const app = await db
      .select({
        id: apps.id,
        name: apps.name,
        ownerId: apps.ownerId,
        koyebAppId: apps.koyebAppId,
        koyebServiceId: apps.koyebServiceId,
        koyebDomainId: apps.koyebDomainId,
        neonProjectId: apps.neonProjectId,
        githubUsername: apps.githubUsername,
        deletedAt: apps.deletedAt,
      })
      .from(apps)
      .where(and(eq(apps.id, id), eq(apps.ownerId, user.id)));

    if (!app || app.length === 0) {
      return reply.status(404).send({
        error: 'App not found',
      });
    }

    const appData = app[0]!;

    // Check if app is already deleted
    if (appData.deletedAt) {
      return reply.status(400).send({
        error: 'App is already deleted',
      });
    }

    logger.info('Starting hybrid app deletion', {
      appId: id,
      appName: appData.name,
      userId: user.id,
    });

    // Phase 1: Hard delete related data (no analytics value)

    // 1. Delete app prompts (conversation history)
    await db.delete(appPrompts).where(eq(appPrompts.appId, id));
    logger.info('Hard deleted app prompts', { appId: id });

    // 2. Delete deployment records for this app
    await db.delete(deployments).where(eq(deployments.appId, id));
    logger.info('Hard deleted deployment records', { appId: id });

    // Phase 2: Soft delete the main app record (preserve for analytics)
    await db
      .update(apps)
      .set({
        deletedAt: new Date(),
        updatedAt: new Date(),
      })
      .where(eq(apps.id, id));

    logger.info('Soft deleted main app record', { appId: id });

    // TODO: Phase 3: Clean up external resources (Koyeb, Neon, ECR)
    // This will be implemented in the next phase to save costs
    if (appData.koyebServiceId || appData.neonProjectId) {
      logger.info('External resource cleanup needed', {
        appId: id,
        hasKoyebService: !!appData.koyebServiceId,
        hasNeonProject: !!appData.neonProjectId,
      });
    }

    logger.info('Hybrid app deletion completed successfully', {
      appId: id,
      appName: appData.name,
    });

    return reply.status(200).send({
      message: 'App deleted successfully',
      appId: id,
    });

  } catch (error) {
    logger.error('Error deleting app', {
      appId: id,
      userId: user.id,
      error: error instanceof Error ? error.message : error,
    });

    return reply.status(500).send({
      error: 'Internal server error while deleting app',
    });
  }
}

