import { and, eq, isNull } from 'drizzle-orm';
import type { FastifyReply, FastifyRequest } from 'fastify';
import { validate } from 'uuid';
import { apps, appPrompts, deployments, db } from '../db';
import {
  deleteKoyebService,
  deleteKoyebDomain,
  deleteKoyebApp,
  getOrganizationToken,
} from '../deploy/koyeb';
import { deleteNeonProject } from '../deploy/neon';
import { deleteECRImages, deleteECRRepository } from '../ecr';
import { logger } from '../logger';

export async function deleteApp(
  request: FastifyRequest,
  reply: FastifyReply,
): Promise<void> {
  const user = request.user;
  const { id } = request.params as { id: string };

  if (!validate(id)) {
    return reply.status(400).send({ error: 'Invalid app ID' });
  }

  try {
    // First, verify the app exists, belongs to the user, and is not already deleted
    const app = await db
      .select({
        id: apps.id,
        name: apps.name,
        ownerId: apps.ownerId,
        koyebAppId: apps.koyebAppId,
        koyebServiceId: apps.koyebServiceId,
        koyebDomainId: apps.koyebDomainId,
        neonProjectId: apps.neonProjectId,
        githubUsername: apps.githubUsername,
        deletedAt: apps.deletedAt,
      })
      .from(apps)
      .where(and(eq(apps.id, id), eq(apps.ownerId, user.id)));

    if (!app || app.length === 0) {
      return reply.status(404).send({
        error: 'App not found',
      });
    }

    const appData = app[0]!;

    // Check if app is already deleted
    if (appData.deletedAt) {
      return reply.status(400).send({
        error: 'App is already deleted',
      });
    }

    // Get organization info before deleting deployment records (needed for Koyeb cleanup)
    const userDeployments = await db
      .select({
        koyebOrgId: deployments.koyebOrgId,
      })
      .from(deployments)
      .where(eq(deployments.ownerId, user.id))
      .limit(1);

    const koyebOrgId = userDeployments[0]?.koyebOrgId;

    logger.info('Starting hybrid app deletion', {
      appId: id,
      appName: appData.name,
      userId: user.id,
    });

    // Phase 1: Hard delete related data (no analytics value)

    // 1. Delete app prompts (conversation history)
    await db.delete(appPrompts).where(eq(appPrompts.appId, id));
    logger.info('Hard deleted app prompts', { appId: id });

    // 2. Delete deployment records for this app (clean up database)
    await db.delete(deployments).where(eq(deployments.appId, id));
    logger.info('Hard deleted deployment records', { appId: id });

    // Phase 2: Soft delete the main app record (preserve for analytics)
    await db
      .update(apps)
      .set({
        deletedAt: new Date(),
        updatedAt: new Date(),
        // Clear external resource IDs (resources will be deleted externally)
        koyebAppId: null,
        koyebServiceId: null,
        koyebDomainId: null,
        neonProjectId: null,
      })
      .where(eq(apps.id, id));

    logger.info('Soft deleted main app record', { appId: id });

    // Phase 3: Clean up external resources (Koyeb - immediate cost savings)
    await cleanupKoyebResources({
      appId: id,
      ownerId: user.id,
      koyebServiceId: appData.koyebServiceId,
      koyebDomainId: appData.koyebDomainId,
      koyebAppId: appData.koyebAppId,
      koyebOrgId: koyebOrgId,
    });

    // Phase 4: Clean up Neon database project (high cost savings)
    await cleanupNeonResources({
      appId: id,
      neonProjectId: appData.neonProjectId,
    });

    // Phase 5: Clean up ECR resources (Docker images storage costs)
    await cleanupECRResources({
      appId: id,
      githubUsername: appData.githubUsername,
    });

    logger.info('Hybrid app deletion completed successfully', {
      appId: id,
      appName: appData.name,
    });

    return reply.status(200).send({
      message: 'App deleted successfully',
      appId: id,
    });
  } catch (error) {
    logger.error('Error deleting app', {
      appId: id,
      userId: user.id,
      error: error instanceof Error ? error.message : error,
    });

    return reply.status(500).send({
      error: 'Internal server error while deleting app',
    });
  }
}

async function cleanupKoyebResources({
  appId,
  ownerId,
  koyebServiceId,
  koyebDomainId,
  koyebAppId,
  koyebOrgId,
}: {
  appId: string;
  ownerId: string;
  koyebServiceId: string | null;
  koyebDomainId: string | null;
  koyebAppId: string | null;
  koyebOrgId: string | null | undefined;
}) {
  // Skip if no Koyeb resources to clean up
  if (!koyebServiceId && !koyebDomainId && !koyebAppId) {
    logger.info('No Koyeb resources to clean up', { appId });
    return;
  }

  try {
    // Use pre-retrieved organization ID (retrieved before deployment deletion)

    if (!koyebOrgId) {
      logger.warn('No Koyeb organization found for user, skipping cleanup', {
        appId,
        ownerId,
      });
      return;
    }

    // Get organization token
    const userToken = await getOrganizationToken(koyebOrgId);

    logger.info('Starting Koyeb resource cleanup', {
      appId,
      hasService: !!koyebServiceId,
      hasDomain: !!koyebDomainId,
      hasApp: !!koyebAppId,
    });

    // Clean up resources in order: Service -> Domain -> App
    // This ensures proper dependency cleanup

    if (koyebServiceId) {
      try {
        await deleteKoyebService({
          serviceId: koyebServiceId,
          token: userToken,
        });
        logger.info('Deleted Koyeb service', {
          appId,
          serviceId: koyebServiceId,
        });
      } catch (error) {
        logger.error('Failed to delete Koyeb service (continuing)', {
          appId,
          serviceId: koyebServiceId,
          error: error instanceof Error ? error.message : error,
        });
        // Continue with other resources even if service deletion fails
      }
    }

    if (koyebDomainId) {
      try {
        await deleteKoyebDomain({
          domainId: koyebDomainId,
          token: userToken,
        });
        logger.info('Deleted Koyeb domain', {
          appId,
          domainId: koyebDomainId,
        });
      } catch (error) {
        logger.error('Failed to delete Koyeb domain (continuing)', {
          appId,
          domainId: koyebDomainId,
          error: error instanceof Error ? error.message : error,
        });
        // Continue with other resources even if domain deletion fails
      }
    }

    if (koyebAppId) {
      try {
        await deleteKoyebApp({
          appId: koyebAppId,
          token: userToken,
        });
        logger.info('Deleted Koyeb app', {
          appId,
          koyebAppId: koyebAppId,
        });
      } catch (error) {
        logger.error('Failed to delete Koyeb app (continuing)', {
          appId,
          koyebAppId: koyebAppId,
          error: error instanceof Error ? error.message : error,
        });
        // This is the final step, so we just log the error
      }
    }

    logger.info('Koyeb resource cleanup completed', { appId });
  } catch (error) {
    // Log the error but don't fail the entire deletion process
    logger.error('Failed to clean up Koyeb resources', {
      appId,
      ownerId,
      error: error instanceof Error ? error.message : error,
    });
    // The app deletion should still succeed even if external cleanup fails
  }
}

async function cleanupNeonResources({
  appId,
  neonProjectId,
}: {
  appId: string;
  neonProjectId: string | null;
}) {
  // Skip if no Neon project to clean up
  if (!neonProjectId) {
    logger.info('No Neon project to clean up', { appId });
    return;
  }

  try {
    logger.info('Starting Neon project cleanup', {
      appId,
      neonProjectId,
    });

    await deleteNeonProject({
      projectId: neonProjectId,
    });

    logger.info('Neon project cleanup completed', {
      appId,
      neonProjectId,
    });
  } catch (error) {
    // Log the error but don't fail the entire deletion process
    logger.error('Failed to clean up Neon project', {
      appId,
      neonProjectId,
      error: error instanceof Error ? error.message : error,
    });
    // The app deletion should still succeed even if Neon cleanup fails
  }
}

async function cleanupECRResources({
  appId,
  githubUsername,
}: {
  appId: string;
  githubUsername: string | null;
}) {
  // Skip if no GitHub username (needed for ECR repository naming)
  if (!githubUsername) {
    logger.info('No GitHub username available, skipping ECR cleanup', {
      appId,
    });
    return;
  }

  try {
    logger.info('Starting ECR resources cleanup', {
      appId,
      githubUsername,
    });

    // Delete Docker images first, then the repository
    // This approach saves storage costs immediately
    await deleteECRImages({
      appId,
      githubUsername,
    });

    await deleteECRRepository({
      appId,
      githubUsername,
    });

    logger.info('ECR resources cleanup completed', {
      appId,
      githubUsername,
    });
  } catch (error) {
    // Log the error but don't fail the entire deletion process
    logger.error('Failed to clean up ECR resources', {
      appId,
      githubUsername,
      error: error instanceof Error ? error.message : error,
    });
    // The app deletion should still succeed even if ECR cleanup fails
  }
}

