    });
    throw new Error(error);
  }

  const responseData = await response.json();
  const secretId = responseData?.secret?.id;
  if (!secretId) {
    throw new Error('Secret ID not found in the response');
  }
  return secretId;
}

export async function updateEcrSecret({
  token,
