  > 630:function getDeploymentChecker({
  > 631:  deploymentId,
  > 632:  token,
  > 633:}: {
  > 634:  deploymentId: string;
  > 635:  token: string;
  > 636:}) {
  > 637:  async function checkDeployment(): Promise<{
  > 638:    message: string;
  > 639:    isDeployed: boolean;
  > 640:    type: DeploymentState;
  > 641:  }> {
  > 642:    try {
  > 643:      const { koyebDeploymentStatus } = await getKoyebDeployment({
  > 644:        deploymentId,
  > 645:        token,
  > 646:      });
  > 647:
  > 648:      logger.info('Deployment status check', {
  > 649:        deploymentId,
  > 650:        status: koyebDeploymentStatus,
  > 651:      });
  > 652:
  > 653:      if (koyebDeploymentStatus === 'HEALTHY') {
  > 654:        logger.info('Deployment is healthy');
  > 655:        return {
  > 656:          message: `Your application has been deployed`,
  > 657:          isDeployed: true,
  > 658:          type: 'HEALTHY',
  > 659:        };
  > 660:      }
  > 661:
  > 662:      if (koyebDeploymentStatus === 'STOPPING') {
  > 663:        logger.info(
  > 664:          'Deployment is stopping, a new deployment is being created',
  > 665:        );
  > 666:        return {
  > 667:          message: `Current deployment is stopping, a new deployment is being created`,
  > 668:          isDeployed: false,
  > 669:          type: 'STOPPING',
  > 670:        };
  > 671:      }
  > 672:
  > 673:      if (koyebDeploymentStatus === 'ERROR') {
  > 674:        logger.error('Deployment has error status');
  > 675:        return {
  > 676:          message: `There was an error deploying your application.`,
  > 677:          isDeployed: false,
  > 678:          type: 'ERROR',
  > 679:        };
  > 680:      }
  > 681:
  > 682:      logger.info('Deployment still in progress', {
  > 683:        status: koyebDeploymentStatus,
  > 684:      });
  > 685:      return {
  > 686:        message: `Your application is being deployed`,
  > 687:        isDeployed: false,
  > 688:        type: 'DEPLOYMENT_IN_PROGRESS',
  > 689:      };
  > 690:    } catch (error) {
  > 691:      logger.error('Error checking deployment status', { error, deploymentId });
  > 692:      return {
  > 693:        message: `Unable to check deployment status`,
  > 694:        isDeployed: false,
  > 695:        type: 'ERROR',
  > 696:      };
  > 697:    }
  > 698:  }
  > 699:
  > 700:  return checkDeployment;
  > 701:}
