  > 703:async function updateDeploymentMessage(
  > 704:  messageId: string,
  > 705:  deploymentType: DeploymentState,
  > 706:  message: string,
  > 707:) {
  > 708:  try {
  > 709:    const newPlatformType =
  > 710:      deploymentType === 'HEALTHY'
  > 711:        ? PlatformMessageType.DEPLOYMENT_COMPLETE
  > 712:        : PlatformMessageType.DEPLOYMENT_FAILED;
  > 713:
  > 714:    const existingMessage = await db
  > 715:      .select()
  > 716:      .from(appPrompts)
  > 717:      .where(eq(appPrompts.id, messageId))
  > 718:      .limit(1);
  > 719:
  > 720:    if (existingMessage.length === 0) {
  > 721:      return;
  > 722:    }
  > 723:
  > 724:    const currentMetadata = existingMessage[0]?.metadata || {};
  > 725:    const updatedMetadata = {
  > 726:      ...currentMetadata,
  > 727:      type: newPlatformType,
  > 728:    };
  > 729:
  > 730:    await db
  > 731:      .update(appPrompts)
  > 732:      .set({
  > 733:        metadata: updatedMetadata,
  > 734:        prompt: message,
  > 735:      })
  > 736:      .where(eq(appPrompts.id, messageId));
  > 737:  } catch (error) {
  > 738:    logger.error('Failed to update deployment message', {
  > 739:      messageId,
  > 740:      deploymentType,
  > 741:      error,
  > 742:    });
  > 743:  }
  > 744:}
