async function getKoyebOrganization({
  orgId,
  token,
}: {
  orgId: string;
  token: string;
}) {
  const response = await fetch(
    `https://app.koyeb.com/v1/organizations/${orgId}`,
    {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${token}`,
      },
    },
  );

  if (!response.ok) {
    const error = await response.text();
    logger.error('Failed to get Koyeb organization', {
      status: response.status,
      statusText: response.statusText,
      error,
      orgId,
    });
    throw new Error(`Failed to get Koyeb organization: ${error}`);
  }

  const data = await response.json();
  const organizationStatus = data?.organization?.status;
  if (!organizationStatus) {
    throw new Error('Organization status not found in the response');
  }

  return { organizationStatus };
}

