// Poll organization status to confirm deactivation
for (let attempt = 1; attempt <= 5; attempt++) {
  try {
    await new Promise(resolve => setTimeout(resolve, 1000));

    const { organizationStatus } = await getKoyebOrganization({
      orgId,
      token,
    });

    logger.info('Organization status check', {
      orgId,
      attempt,
      status: organizationStatus,
    });

    if (organizationStatus === 'inactive' || organizationStatus === 'deactivated') {
      logger.info('Successfully deactivated Koyeb organization', { orgId });
      return { deactivated: true };
    }

    if (attempt === 5) {
      const error = `Organization is still active after ${attempt} attempts`;
      logger.error('Failed to confirm Koyeb organization deactivation', {
        orgId,
        status: organizationStatus,
        attempts: attempt,
      });

      Instrumentation.captureError(
        new Error(`Failed to confirm Koyeb organization deactivation: ${error}`),
        {
          context: 'deactivate_koyeb_organization_polling',
          orgId,
        },
      );

      throw new Error(`Failed to confirm Koyeb organization deactivation: ${error}`);
    }
  } catch (pollingError) {
    logger.error('Error during organization status polling', {
      orgId,
      attempt,
      error: pollingError,
    });

    if (attempt === 5) {
      throw new Error(`Failed to verify organization deactivation after ${attempt} attempts: ${pollingError}`);
    }
  }
}

