  > 488:export const getKoyebDeploymentEndpoint = async (
  > 489:  request: FastifyRequest<{
  > 490:    Params: { id: string };
  > 491:    Querystring: { messageId?: string };
  > 492:  }>,
  > 493:  reply: FastifyReply,
  > 494:) => {
  > 495:  try {
  > 496:    const { id } = request.params;
  > 497:    const { messageId } = request.query;
  > 498:
  > 499:    if (!id) {
  > 500:      return reply.status(400).send({ message: 'Deployment ID is required' });
  > 501:    }
  > 502:
  > 503:    const deploymentData = await db
  > 504:      .select({
  > 505:        koyebOrgId: deployments.koyebOrgId,
  > 506:      })
  > 507:      .from(deployments)
  > 508:      .where(
  > 509:        and(
  > 510:          eq(deployments.ownerId, request.user.id!),
  > 511:          isNull(deployments.deletedAt),
  > 512:        ),
  > 513:      )
  > 514:      .orderBy(desc(deployments.createdAt))
  > 515:      .limit(1);
  > 516:
  > 517:    const koyebOrgId = deploymentData[0]?.koyebOrgId;
  > 518:
  > 519:    if (!koyebOrgId) {
  > 520:      return reply
  > 521:        .status(404)
  > 522:        .send({ message: 'Organization for user not found' });
  > 523:    }
  > 524:
  > 525:    const userToken = await getOrganizationToken(koyebOrgId);
  > 526:
  > 527:    const deploymentChecker = getDeploymentChecker({
  > 528:      deploymentId: id,
  > 529:      token: userToken,
  > 530:    });
  > 531:
  > 532:    const response = await deploymentChecker();
  > 533:
  > 534:    // if received messageId, should update the message with the deployment status
  > 535:    if (
  > 536:      messageId &&
  > 537:      (response.type === 'HEALTHY' || response.type === 'ERROR')
  > 538:    ) {
  > 539:      await updateDeploymentMessage(messageId, response.type, response.message);
  > 540:    }
  > 541:
  > 542:    return reply.send(response);
  > 543:  } catch (error) {
  > 544:    logger.error('Error getting deployment endpoint', { error });
  > 545:    return reply.status(500).send({ message: 'Internal server error' });
  > 546:  }
  > 547:};
