  > 549:function getOrgName(githubUsername: string) {
  > 550:  if (process.env.NODE_ENV === 'production') {
  > 551:    return getNormalizedOrgName(`appbuild-${githubUsername}`);
  > 552:  }
  > 553:
  > 554:  return getNormalizedOrgName(`appbuild-dev-${githubUsername}`);
  > 555:}
  > 556:
  > 557:/**
  > 558: * Koyeb validation for org names is this
  > 559: * "Use 1 to 39 letters, digits and dash only"
  > 560: * So this function is used to normalize the org name to meet the requirements.
  > 561: */
  > 562:function getNormalizedOrgName(orgName: string) {
  > 563:  // This will replace all non-alphanumeric characters with a dash
  > 564:  let normalized = orgName.replace(/[^a-zA-Z0-9-]/g, '-');
  > 565:
  > 566:  // If there's multiple dashes, they get replaced with one.
  > 567:  normalized = normalized.replace(/-+/g, '-');
  > 568:
  > 569:  if (normalized.length > 39) {
  > 570:    normalized = normalized.substring(0, 39);
  > 571:  }
  > 572:
  > 573:  return normalized;
  > 574:}
