async function pollOrganizationDeactivationInBackground({
  orgId,
  token,
}: {
  orgId: string;
  token: string;
}) {
  const delays = [500, 1000, 1500, 1500, 1500]; // 0.5s, 1s, 1.5s, 1.5s, 1.5s

  const poll = async (attempt: number) => {
    if (attempt > 5) {
      logger.error('Failed to confirm Koyeb organization deactivation after 5 attempts', {
        orgId,
      });

      Instrumentation.captureError(
        new Error('Failed to confirm Koyeb organization deactivation after 5 attempts'),
        {
          context: 'deactivate_koyeb_organization_background_polling',
          orgId,
        },
      );
      return;
    }

    try {
      const { organization } = await getKoyebOrganization({
        orgId,
        token,
      });

      logger.info('Background organization status check', {
        orgId,
        attempt,
        status: organization.status,
      });

      if (organization.status === 'DEACTIVATED') {
        logger.info('Successfully confirmed Koyeb organization deactivation', { orgId });
        return;
      }

      // Schedule next attempt
      setTimeout(() => {
        poll(attempt + 1);
      }, delays[attempt - 1] || 1500);

    } catch (pollingError) {
      logger.error('Error during background organization status polling', {
        orgId,
        attempt,
        error: pollingError,
      });

      // Continue polling unless it's the final attempt
      if (attempt < 5) {
        setTimeout(() => {
          poll(attempt + 1);
        }, delays[attempt - 1] || 1500);
      } else {
        Instrumentation.captureError(
          new Error(`Failed to verify organization deactivation after ${attempt} attempts: ${pollingError}`),
          {
            context: 'deactivate_koyeb_organization_background_polling',
            orgId,
          },
        );
      }
    }
  };

  // Start the first poll after initial delay
  setTimeout(() => {
    poll(1);
  }, delays[0]);
}

