// Poll organization status to confirm deactivation with exponential backoff
const delays = \[500, 1000, 1500, 1500, 1500\]; // 0\.5s, 1s, 1\.5s, 1\.5s, 1\.5s

for \(let attempt = 1; attempt <= 5; attempt\+\+\) \{
  try \{
    await new Promise\(\(resolve\) => setTimeout\(resolve, delays\[attempt - 1\]\)\);

    const \{ organization \} = await getKoyebOrganization\(\{
      orgId,
      token,
    \}\);

    logger\.info\('Organization status check', \{
      orgId,
      attempt,
      status: organization\.status,
    \}\);

    if \(organization\.status === 'DEACTIVATED'\) \{
      logger\.info\('Successfully deactivated Koyeb organization', \{ orgId \}\);
      return \{ deactivated: true \};
    \}

    if \(attempt === 5\) \{
      const error = `Organization is still active after \$\{attempt\} attempts`;
      logger\.error\('Failed to confirm Koyeb organization deactivation', \{
        orgId,
        status: organization\.status,
        attempts: attempt,
      \}\);

      Instrumentation\.captureError\(
        new Error\(
          `Failed to confirm Koyeb organization deactivation: \$\{error\}`,
        \),
        \{
          context: 'deactivate_koyeb_organization_polling',
          orgId,
        \},
      \);

      throw new Error\(
        `Failed to confirm Koyeb organization deactivation: \$\{error\}`,
      \);
    \}
  \} catch \(pollingError\) \{
    logger\.error\('Error during organization status polling', \{
      orgId,
      attempt,
      error: pollingError,
    \}\);

    if \(attempt === 5\) \{
      throw new Error\(
        `Failed to verify organization deactivation after \$\{attempt\} attempts: \$\{pollingError\}`,
      \);
    \}
  \}
\}

