input.body:
function pollOrganizationDeactivationInBackground({
  orgId,
  token,
}: {
  orgId: string;
  token: string;
}): Promise<{ deactivated: true }> {
  const delays = [500, 1000, 1500, 1500, 1500]; // 0.5s, 1s, 1.5s, 1.5s, 1.5s

  return new Promise((resolve, reject) => {
    const poll = async (attempt: number) => {
      if (attempt > 5) {
        const error = 'Failed to confirm Koyeb organization deactivation after 5 attempts';
        logger.error(error, { orgId });

        Instrumentation.captureError(
          new Error(error),
          {
            context: 'deactivate_koyeb_organization_background_polling',
            orgId,
          },
        );
        reject(new Error(error));
        return;
      }

      try {
        const { organization } = await getKoyebOrganization({
          orgId,
          token,
        });

        logger.info('Background organization status check', {
          orgId,
          attempt,
          status: organization.status,
        });

        if (organization.status === 'DEACTIVATED') {
          logger.info('Successfully confirmed Koyeb organization deactivation', { orgId });
          resolve({ deactivated: true });
          return;
        }

        // Schedule next attempt with non-blocking timeout
        setTimeout(() => {
          poll(attempt + 1);
        }, delays[attempt - 1] || 1500);

      } catch (pollingError) {
        logger.error('Error during background organization status polling', {
          orgId,
          attempt,
          error: pollingError,
        });

        // Continue polling unless it's the final attempt
        if (attempt < 5) {
          setTimeout(() => {
            poll(attempt + 1);
          }, delays[attempt - 1] || 1500);
        } else {
          const finalError = new Error(`Failed to verify organization deactivation after ${attempt} attempts: ${pollingError}`);
          Instrumentation.captureError(
            finalError,
            {
              context: 'deactivate_koyeb_organization_background_polling',
              orgId,
            },
          );
          reject(finalError);
        }
      }
    };

    // Start the first poll after initial delay
    setTimeout(() => {
      poll(1);
    }, delays[0]);
  });
}

