[{"name_path": "deactivateKoyebOrganization", "kind": "Function", "body_location": {"start_line": 1048, "end_line": 1091}, "body": "async function deactivateKoyebOrganization({
  orgId,
  token,
}: {
  orgId: string;
  token: string;
}) {
  const response = await fetch(
    `https://app.koyeb.com/v1/organizations/${orgId}/deactivate`,
    {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        skip_confirmation: true,
      }),
    },
  );

  if (!response.ok) {
    const error = await response.text();
    logger.error('Failed to deactivate Koyeb organization', {
      status: response.status,
      statusText: response.statusText,
      error,
      orgId,
    });

    Instrumentation.captureError(
      new Error(`Failed to deactivate Koyeb organization: ${error}`),
      {
        context: 'deactivate_koyeb_organization',
        orgId,
      },
    );

    throw new Error(`Failed to deactivate Koyeb organization: ${error}`);
  }

  // Start background polling to confirm deactivation (non-blocking but awaitable)
  logger.info('Koyeb organization deactivation initiated', { orgId });
  return pollOrganizationDeactivationInBackground({ orgId, token });
}", "relative_path": "apps/backend/src/deploy/koyeb.ts"}]

