     1â†’import { exec as execNative } from 'node:child_process';
     2â†’import { eq } from 'drizzle-orm';
     3â†’import { apps, db } from '../db';
     4â†’import { logger } from '../logger';
     5â†’import { promisify } from 'node:util';
     6â†’import type { App } from '../db/schema';
     7â†’import { getOrCreateNeonProject } from './neon';
     8â†’import fs from 'node:fs';
     9â†’import { DeployStatus } from '@appdotbuild/core';
    10â†’import z from 'zod';
    11â†’
    12â†’const exec = promisify(execNative);
    13â†’
    14â†’const DATABASE_URL_ENV_KEY = 'APP_DATABASE_URL';
    15â†’const DATABASE_URL_RESOURCE_KEY = 'APP_DATABASE_URL';
    16â†’
    17â†’const databricksScopeSchema = z.object({
    18â†’  key: z.string(),
    19â†’  value: z.string(),
    20â†’});
    21â†’
    22â†’export async function deployToDatabricks({
    23â†’  appId,
    24â†’  appDirectory,
    25â†’  currentApp,
    26â†’}: {
    27â†’  appId: string;
    28â†’  appDirectory: string;
    29â†’  currentApp: Partial<App>;
    30â†’}) {
    31â†’  // Validate appId to prevent shell injection
    32â†’  validateAppId(appId);
    33â†’
    34â†’  if (!currentApp.databricksApiKey || !currentApp.databricksHost) {
    35â†’    throw new Error(
    36â†’      'Databricks API key and host are required for Databricks deployment',
    37â†’    );
    38â†’  }
    39â†’
    40â†’  const { connectionString, neonProjectId } = await getOrCreateNeonProject({
    41â†’    existingNeonProjectId: currentApp.neonProjectId ?? undefined,
    42â†’  });
    43â†’
    44â†’  // Update app status to deploying for databricks deployments
    45â†’  await db
    46â†’    .update(apps)
    47â†’    .set({
    48â†’      deployStatus: DeployStatus.DEPLOYING,
    49â†’      neonProjectId,
    50â†’    })
    51â†’    .where(eq(apps.id, appId));
    52â†’
    53â†’  // Create isolated environment variables for this deployment
    54â†’  const databricksEnv = {
    55â†’    ...process.env,
    56â†’    DATABRICKS_HOST: currentApp.databricksHost,
    57â†’    DATABRICKS_TOKEN: currentApp.databricksApiKey,
    58â†’  };
    59â†’
    60â†’  // Generate a unique workspace path for this app
    61â†’  const shortAppId = appId.slice(0, 8);
    62â†’  const appName = `app-${shortAppId}`;
    63â†’
    64â†’  const workspaceSourceCodePath = `/${appName}`;
    65â†’
    66â†’  logger.info('Starting Databricks deployment', {
    67â†’    appId,
    68â†’    workspaceSourceCodePath,
    69â†’    databricksHost: currentApp.databricksHost,
    70â†’  });
    71â†’
    72â†’  try {
    73â†’    // 1. Check if the app exists
    74â†’    const appExists = await checkDatabricksAppExists({
    75â†’      appName,
    76â†’      databricksHost: currentApp.databricksHost,
    77â†’      databricksApiKey: currentApp.databricksApiKey,
    78â†’    });
    79â†’
    80â†’    // 2. Create a databricks app IF it doesn't exist
    81â†’    if (appExists) {
    82â†’      logger.info(`Databricks app ${appName} already exists`);
    83â†’    } else {
    84â†’      logger.info(`Creating Databricks app ${appName}`);
    85â†’      await exec(`databricks apps create ${appName}`, {
    86â†’        env: databricksEnv,
    87â†’      });
    88â†’    }
    89â†’
    90â†’    // 3. Check if the scope exists
    91â†’    const scopeName = appName;
    92â†’    const scope = await checkIfScopeAlreadyExists({
    93â†’      scopeName,
    94â†’      databricksHost: currentApp.databricksHost,
    95â†’      databricksApiKey: currentApp.databricksApiKey,
    96â†’      secretName: DATABASE_URL_ENV_KEY,
    97â†’    });
    98â†’
    99â†’    if (scope) {
   100â†’      logger.info(`Scope ${scopeName} already exists`);
   101â†’    } else {
   102â†’      logger.info(`Scope ${appName} does not exist, creating it`);
   103â†’      // 3. setup secrets in databricks
   104â†’      await setupDatabricksAppSecrets({
   105â†’        scopeName,
   106â†’        databricksHost: currentApp.databricksHost,
   107â†’        databricksApiKey: currentApp.databricksApiKey,
   108â†’        secrets: {
   109â†’          [DATABASE_URL_ENV_KEY]: connectionString,
   110â†’        },
   111â†’      });
   112â†’    }
   113â†’
   114â†’    // 4. Create a databricks config file, so the app can access Databricks secrets
   115â†’    const databricksConfigFile = createDatabricksConfigFile([
   116â†’      {
   117â†’        sectionName: DATABASE_URL_RESOURCE_KEY,
   118â†’        secretSection: {
   119â†’          scope: scopeName,
   120â†’          key: DATABASE_URL_ENV_KEY,
   121â†’        },
   122â†’      },
   123â†’    ]);
   124â†’    fs.writeFileSync(`${appDirectory}/databricks.yml`, databricksConfigFile);
   125â†’
   126â†’    // 5. Create a databricks app file, to define the app's environment variables
   127â†’    const databricksAppFile = createDatabricksAppFile([
   128â†’      {
   129â†’        name: DATABASE_URL_ENV_KEY,
   130â†’        value: connectionString,
   131â†’        isSecret: false,
   132â†’
   133â†’        // TODO: use this instead, when we figure out how to get the secret value from the scope
   134â†’        // value: DATABASE_URL_ENV_KEY,
   135â†’        // isSecret: true,
   136â†’      },
   137â†’    ]);
   138â†’    fs.writeFileSync(`${appDirectory}/app.yaml`, databricksAppFile);
   139â†’
   140â†’    // 6. Import the code into the databricks workspace (after all files are created)
   141â†’    logger.info('Importing code to Databricks workspace', {
   142â†’      appId,
   143â†’      workspaceSourceCodePath,
   144â†’      databricksHost: currentApp.databricksHost,
   145â†’    });
   146â†’    await exec(
   147â†’      `databricks workspace import-dir --overwrite "${appDirectory}" "${workspaceSourceCodePath}"`,
   148â†’      {
   149â†’        cwd: appDirectory,
   150â†’        env: databricksEnv,
   151â†’      },
   152â†’    );
   153â†’
   154â†’    // 7. Deploy the app from there
   155â†’    logger.info('Deploying app to Databricks');
   156â†’    const deployResult = await exec(
   157â†’      `databricks apps deploy ${appName} --source-code-path /Workspace${workspaceSourceCodePath}`,
   158â†’      {
   159â†’        cwd: appDirectory,
   160â†’        env: databricksEnv,
   161â†’      },
   162â†’    );
   163â†’    logger.info('Databricks deployment completed', {
   164â†’      appId,
   165â†’      deployOutput: deployResult.stdout,
   166â†’    });
   167â†’
   168â†’    const appUrl = (
   169â†’      await exec(`databricks apps get ${appName} | jq -r '.url'`, {
   170â†’        env: databricksEnv,
   171â†’      })
   172â†’    ).stdout.trim();
   173â†’
   174â†’    // Update app status to deployed
   175â†’    await db
   176â†’      .update(apps)
   177â†’      .set({
   178â†’        deployStatus: DeployStatus.DEPLOYED,
   179â†’        appUrl,
   180â†’      })
   181â†’      .where(eq(apps.id, appId));
   182â†’
   183â†’    return {
   184â†’      appURL: appUrl,
   185â†’      deploymentId: `databricks-${appId}`,
   186â†’      deployStatus: DeployStatus.DEPLOYED,
   187â†’    };
   188â†’  } catch (error) {
   189â†’    logger.error('Databricks deployment failed', {
   190â†’      appId,
   191â†’      error: error instanceof Error ? error.message : String(error),
   192â†’    });
   193â†’
   194â†’    // Update app status to failed
   195â†’    await db
   196â†’      .update(apps)
   197â†’      .set({
   198â†’        deployStatus: DeployStatus.FAILED,
   199â†’      })
   200â†’      .where(eq(apps.id, appId));
   201â†’
   202â†’    throw new Error(
   203â†’      `Databricks deployment failed: ${
   204â†’        error instanceof Error ? error.message : String(error)
   205â†’      }`,
   206â†’    );
   207â†’  }
   208â†’}
   209â†’
   210â†’async function checkDatabricksAppExists({
   211â†’  appName,
   212â†’  databricksHost,
   213â†’  databricksApiKey,
   214â†’}: {
   215â†’  appName: string;
   216â†’  databricksHost: string;
   217â†’  databricksApiKey: string;
   218â†’}) {
   219â†’  try {
   220â†’    const result = await exec(
   221â†’      `databricks apps list | grep ${appName} || true`,
   222â†’      {
   223â†’        env: {
   224â†’          ...process.env,
   225â†’          DATABRICKS_HOST: databricksHost,
   226â†’          DATABRICKS_TOKEN: databricksApiKey,
   227â†’        },
   228â†’      },
   229â†’    );
   230â†’
   231â†’    if (result.stderr) {
   232â†’      logger.warn('Databricks app check stderr output', {
   233â†’        appName,
   234â†’        stderr: result.stderr,
   235â†’      });
   236â†’    }
   237â†’
