1:import { and, eq, isNull } from 'drizzle-orm';
2:import type { FastifyReply, FastifyRequest } from 'fastify';
3:import { validate } from 'uuid';
4:import { apps, appPrompts, deployments, db } from '../db';
5:import {
6:  deleteKoyebService,
7:  deleteKoyebDomain,
8:  deleteKoyebApp,
9:  deleteKoyebOrganization,
10:  getOrganizationToken,
11:} from '../deploy/koyeb';
12:import { deleteNeonProject } from '../deploy/neon';
13:import { deleteECRImages, deleteECRRepository } from '../ecr';
14:import { DEFAULT_OWNER, GithubEntity } from '../github/entity';
15:import { archiveRepository } from '../github/delete-repository';
16:import { logger } from '../logger';
17:import { Instrumentation } from '../instrumentation';
18:
19:export async function deleteApp(
20:  request: FastifyRequest,
21:  reply: FastifyReply,
22:): Promise<void> {
23:  const user = request.user;
24:  const { id } = request.params as { id: string };
25:
26:  if (!validate(id)) {
27:    return reply.status(400).send({ error: 'Invalid app ID' });
28:  }
29:
30:  try {
31:    const app = await db
32:      .select({
33:        id: apps.id,
34:        name: apps.name,
35:        ownerId: apps.ownerId,
36:        koyebAppId: apps.koyebAppId,
37:        koyebServiceId: apps.koyebServiceId,
38:        koyebDomainId: apps.koyebDomainId,
39:        neonProjectId: apps.neonProjectId,
40:        githubUsername: apps.githubUsername,
41:        repositoryUrl: apps.repositoryUrl,
42:        deletedAt: apps.deletedAt,
43:      })
44:      .from(apps)
45:      .where(and(eq(apps.id, id), eq(apps.ownerId, user.id)));
46:
47:    if (!app || app.length === 0) {
48:      return reply.status(404).send({
49:        error: 'App not found',
50:      });
51:    }
52:
53:    const appData = app[0]!;
54:
55:    // Check if app is already deleted
56:    if (appData.deletedAt) {
57:      return reply.status(400).send({
58:        error: 'App is already deleted',
59:      });
60:    }
61:
62:    // Get organization info and check remaining deployments (for Koyeb cleanup decisions)
63:    const userDeployments = await db
64:      .select({
65:        koyebOrgId: deployments.koyebOrgId,
66:        appId: deployments.appId,
67:      })
68:      .from(deployments)
69:      .where(
70:        and(eq(deployments.ownerId, user.id), isNull(deployments.deletedAt)),
71:      );
72:
73:    const koyebOrgId = userDeployments[0]?.koyebOrgId;
74:    const remainingDeployments = userDeployments.filter((d) => d.appId !== id);
75:    const isLastActiveDeployment = remainingDeployments.length === 0;
76:
77:    await db.delete(appPrompts).where(eq(appPrompts.appId, id));
78:    logger.info('Hard deleted app prompts', { appId: id });
79:
80:    await db
81:      .update(deployments)
82:      .set({ deletedAt: new Date() })
83:      .where(eq(deployments.appId, id));
84:    logger.info('Soft deleted deployment records', { appId: id });
85:
86:    // Phase 2: Soft delete the main app record (preserve for analytics)
87:    await db
88:      .update(apps)
89:      .set({
90:        deletedAt: new Date(),
91:        updatedAt: new Date(),
92:        koyebAppId: null,
93:        koyebServiceId: null,
94:        koyebDomainId: null,
95:        neonProjectId: null,
96:      })
97:      .where(eq(apps.id, id));
98:    logger.info('Soft deleted main app record', { appId: id });
99:
100:    await cleanupKoyebResources({
101:      appId: id,
102:      ownerId: user.id,
103:      koyebServiceId: appData.koyebServiceId,
104:      koyebDomainId: appData.koyebDomainId,
105:      koyebAppId: appData.koyebAppId,
106:      koyebOrgId: koyebOrgId,
107:      isLastActiveDeployment: isLastActiveDeployment,
108:    });
109:
110:    await cleanupNeonResources({
111:      appId: id,
112:      neonProjectId: appData.neonProjectId,
113:    });
114:
115:    await cleanupECRResources({
116:      appId: id,
117:      githubUsername: appData.githubUsername,
118:    });
119:
120:    await cleanupGithubResources({
121:      appId: id,
122:      repositoryUrl: appData.repositoryUrl,
123:      githubUsername: appData.githubUsername,
124:      githubAccessToken: user.githubAccessToken,
125:    });
126:
127:    return reply.status(200).send({
128:      message: 'App deleted successfully',
129:      appId: id,
130:    });
131:  } catch (error) {
132:    logger.error('Error deleting app', {
133:      appId: id,
134:      userId: user.id,
135:      error: error instanceof Error ? error.message : error,
136:    });
137:
138:    Instrumentation.captureError(error as Error, {
139:      context: 'delete_app_main',
140:      appId: id,
141:      userId: user.id,
142:    });
143:
144:    return reply.status(500).send({
145:      error: 'Internal server error while deleting app',
146:    });
147:  }
148:}
