315:async function cleanupNeonResources({
316:  appId,
317:  neonProjectId,
318:}: {
319:  appId: string;
320:  neonProjectId: string | null;
321:}) {
322:  if (!neonProjectId) {
323:    logger.info('No Neon project to clean up', { appId });
