331:    });
332:
333:    await deleteNeonProject({
334:      projectId: neonProjectId,
335:    });
336:
337:    logger.info('Neon project cleanup completed', {
338:      appId,
339:      neonProjectId,
340:    });
341:  } catch (error) {
342:    logger.error('Failed to clean up Neon project', {
343:      appId,
344:      neonProjectId,
345:      error: error instanceof Error ? error.message : error,
346:    });
347:
348:    Instrumentation.captureError(error as Error, {
349:      context: 'cleanup_neon_resources',
350:      appId,
351:      neonProjectId,
352:    });
353:  }
354:}
