356:async function cleanupECRResources({
357:  appId,
358:  githubUsername,
359:}: {
360:  appId: string;
361:  githubUsername: string | null;
362:}) {
363:  if (!githubUsername) {
364:    logger.info('No GitHub username available, skipping ECR cleanup', {
365:      appId,
366:    });
367:    return;
