405:async function cleanupGithubResources({
406:  appId,
407:  repositoryUrl,
408:  githubUsername,
409:  githubAccessToken,
410:}: {
411:  appId: string;
412:  repositoryUrl: string | null;
413:  githubUsername: string | null;
414:  githubAccessToken: string;
415:}) {
416:  if (!repositoryUrl || !githubUsername) {
417:    logger.info(
418:      'No GitHub repository URL or username available, skipping GitHub cleanup',
419:      {
420:        appId,
421:        hasRepositoryUrl: !!repositoryUrl,
422:        hasGithubUsername: !!githubUsername,
423:      },
424:    );
425:    return;
426:  }
427:
428:  try {
429:    // Parse repository URL to extract owner and repo name
430:    // Expected format: https://github.com/owner/repo
431:    const urlMatch = repositoryUrl.match(/github\.com\/([^\/]+)\/([^\/]+)/);
432:    if (!urlMatch) {
433:      logger.warn('Invalid GitHub repository URL format, skipping cleanup', {
434:        appId,
435:        repositoryUrl,
436:      });
437:      return;
438:    }
439:
440:    const owner = urlMatch[1];
441:    const repo = urlMatch[2];
442:
443:    if (!owner || !repo) {
444:      logger.warn(
445:        'Could not extract owner/repo from GitHub URL, skipping cleanup',
446:        {
447:          appId,
448:          repositoryUrl,
449:        },
450:      );
451:      return;
452:    }
453:
454:    // Only archive repositories from the appdotbuilder organization
455:    if (owner !== DEFAULT_OWNER) {
456:      logger.info(
457:        `Repository is not from ${DEFAULT_OWNER} organization, skipping cleanup`,
458:        {
459:          appId,
460:          owner,
461:          repo,
462:        },
463:      );
464:      return;
465:    }
466:
467:    logger.info('Starting GitHub repository cleanup', {
468:      appId,
469:      owner,
470:      repo,
471:      repositoryUrl,
472:    });
473:
474:    const githubEntity = new GithubEntity(githubUsername, githubAccessToken);
475:    const initializedEntity = await githubEntity.init();
476:    initializedEntity.repo = repo;
477:
478:    const result = await archiveRepository({
479:      githubEntity: initializedEntity,
480:    });
481:
482:    if (result.statusCode === 200) {
483:      logger.info('GitHub repository archived successfully', {
484:        appId,
485:        owner,
486:        repo,
487:        result,
488:      });
489:    } else {
490:      logger.error('Failed to archive GitHub repository', {
491:        appId,
492:        owner,
493:        repo,
494:        error: result.error,
495:      });
496:    }
497:
498:    logger.info('GitHub repository cleanup completed', {
499:      appId,
500:      owner,
501:      repo,
502:    });
503:  } catch (error) {
504:    logger.error('Failed to clean up GitHub repository', {
505:      appId,
506:      repositoryUrl,
507:      error: error instanceof Error ? error.message : error,
508:    });
509:
510:    Instrumentation.captureError(error as Error, {
511:      context: 'cleanup_github_resources',
512:      appId,
513:      repositoryUrl,
514:    });
515:  }
516:}

