150:async function cleanupKoyebResources({
151:  appId,
152:  ownerId,
153:  koyebServiceId,
154:  koyebDomainId,
155:  koyebAppId,
156:  koyebOrgId,
157:  isLastActiveDeployment,
158:}: {
159:  appId: string;
160:  ownerId: string;
161:  koyebServiceId: string | null;
162:  koyebDomainId: string | null;
163:  koyebAppId: string | null;
164:  koyebOrgId: string | null | undefined;
165:  isLastActiveDeployment: boolean;
166:}) {
167:  // Skip if no Koyeb resources to clean up
168:  if (!koyebServiceId && !koyebDomainId && !koyebAppId) {
169:    logger.info('No Koyeb resources to clean up', { appId });
170:    return;
171:  }
172:
173:  try {
174:    if (!koyebOrgId) {
175:      logger.warn('No Koyeb organization found for user, skipping cleanup', {
176:        appId,
177:        ownerId,
178:      });
179:      return;
180:    }
181:
182:    const userToken = await getOrganizationToken(koyebOrgId);
183:
184:    logger.info('Starting Koyeb resource cleanup', {
185:      appId,
186:      hasService: !!koyebServiceId,
187:      hasDomain: !!koyebDomainId,
188:      hasApp: !!koyebAppId,
189:    });
190:
191:    if (koyebServiceId) {
192:      try {
193:        await deleteKoyebService({
194:          serviceId: koyebServiceId,
195:          token: userToken,
196:        });
197:        logger.info('Deleted Koyeb service', {
198:          appId,
199:          serviceId: koyebServiceId,
200:        });
201:      } catch (error) {
202:        logger.error('Failed to delete Koyeb service (continuing)', {
203:          appId,
204:          serviceId: koyebServiceId,
205:          error: error instanceof Error ? error.message : error,
206:        });
207:
208:        Instrumentation.captureError(error as Error, {
209:          context: 'delete_koyeb_service',
210:          appId,
211:          serviceId: koyebServiceId,
212:        });
213:      }
214:    }
215:
216:    if (koyebDomainId) {
217:      try {
218:        await deleteKoyebDomain({
219:          domainId: koyebDomainId,
220:          token: userToken,
221:        });
222:        logger.info('Deleted Koyeb domain', {
223:          appId,
224:          domainId: koyebDomainId,
225:        });
226:      } catch (error) {
227:        logger.error('Failed to delete Koyeb domain (continuing)', {
228:          appId,
229:          domainId: koyebDomainId,
230:          error: error instanceof Error ? error.message : error,
231:        });
232:
233:        Instrumentation.captureError(error as Error, {
234:          context: 'delete_koyeb_domain',
235:          appId,
236:          domainId: koyebDomainId,
237:        });
238:      }
239:    }
240:
241:    if (koyebAppId) {
242:      try {
243:        await deleteKoyebApp({
244:          appId: koyebAppId,
245:          token: userToken,
246:        });
247:        logger.info('Deleted Koyeb app', {
248:          appId,
249:          koyebAppId: koyebAppId,
250:        });
251:      } catch (error) {
252:        logger.error('Failed to delete Koyeb app (continuing)', {
253:          appId,
254:          koyebAppId: koyebAppId,
255:          error: error instanceof Error ? error.message : error,
256:        });
257:
258:        Instrumentation.captureError(error as Error, {
259:          context: 'delete_koyeb_app',
260:          appId,
261:          koyebAppId: koyebAppId,
262:        });
263:      }
264:    }
265:
266:    if (isLastActiveDeployment && koyebOrgId) {
267:      logger.info(
268:        'Initiating async Koyeb organization deletion (last active deployment)',
269:        {
270:          appId,
271:          koyebOrgId,
272:          ownerId,
273:        },
274:      );
275:
276:      deleteKoyebOrganization({
277:        orgId: koyebOrgId,
278:        token: userToken,
279:      }).catch((error) => {
280:        logger.error('Async Koyeb organization deletion failed', {
281:          appId,
282:          koyebOrgId,
283:          error: error instanceof Error ? error.message : error,
284:        });
285:        Instrumentation.captureError(error as Error, {
286:          context: 'async_delete_koyeb_organization',
287:          appId,
288:          koyebOrgId,
289:        });
290:      });
291:    } else {
292:      logger.info('Keeping Koyeb organization (user has other deployments)', {
293:        appId,
294:        koyebOrgId,
295:        isLastActiveDeployment,
296:      });
297:    }
298:
299:    logger.info('Koyeb resource cleanup completed', { appId });
300:  } catch (error) {
301:    logger.error('Failed to clean up Koyeb resources', {
302:      appId,
303:      ownerId,
304:      error: error instanceof Error ? error.message : error,
305:    });
306:
307:    Instrumentation.captureError(error as Error, {
308:      context: 'cleanup_koyeb_resources',
309:      appId,
310:      ownerId,
311:    });
312:  }
313:}
