import { exec as execNative } from 'node:child_process';
import { eq } from 'drizzle-orm';
import { apps, db } from '../db';
import { logger } from '../logger';
import { promisify } from 'node:util';
import type { App } from '../db/schema';
import { getOrCreateNeonProject } from './neon';
import fs from 'node:fs';
import { DeployStatus } from '@appdotbuild/core';
import z from 'zod';

const exec = promisify(execNative);

const DATABASE_URL_ENV_KEY = 'APP_DATABASE_URL';
const DATABASE_URL_RESOURCE_KEY = 'APP_DATABASE_URL';

const databricksScopeSchema = z.object({
  key: z.string(),
  value: z.string(),
});

export async function deployToDatabricks({
  appId,
  appDirectory,
  currentApp,
}: {
  appId: string;
  appDirectory: string;
  currentApp: Partial<App>;
}) {
  // Validate appId to prevent shell injection
  validateAppId(appId);

  if (!currentApp.databricksApiKey || !currentApp.databricksHost) {
    throw new Error(
      'Databricks API key and host are required for Databricks deployment',
    );
  }

  const { connectionString, neonProjectId } = await getOrCreateNeonProject({
    existingNeonProjectId: currentApp.neonProjectId ?? undefined,
  });

  // Update app status to deploying for databricks deployments
  await db
    .update(apps)
    .set({
      deployStatus: DeployStatus.DEPLOYING,
      neonProjectId,
    })
    .where(eq(apps.id, appId));

  // Create isolated environment variables for this deployment
  const databricksEnv = {
    ...process.env,
    DATABRICKS_HOST: currentApp.databricksHost,
    DATABRICKS_TOKEN: currentApp.databricksApiKey,
  };

  // Generate a unique workspace path for this app
  const shortAppId = appId.slice(0, 8);
  const appName = `app-${shortAppId}`;

  const workspaceSourceCodePath = `/${appName}`;

  logger.info('Starting Databricks deployment', {
    appId,
    workspaceSourceCodePath,
    databricksHost: currentApp.databricksHost,
  });

  try {
    // 1. Check if the app exists
    const appExists = await checkDatabricksAppExists({
      appName,
      databricksHost: currentApp.databricksHost,
      databricksApiKey: currentApp.databricksApiKey,
    });

    // 2. Create a databricks app IF it doesn't exist
    if (appExists) {
      logger.info(`Databricks app ${appName} already exists`);
    } else {
      logger.info(`Creating Databricks app ${appName}`);
      await exec(`databricks apps create ${appName}`, {
        env: databricksEnv,
      });
    }

    // 3. Check if the scope exists
    const scopeName = appName;
    const scope = await checkIfScopeAlreadyExists({
      scopeName,
      databricksHost: currentApp.databricksHost,
      databricksApiKey: currentApp.databricksApiKey,
      secretName: DATABASE_URL_ENV_KEY,
    });

    if (scope) {
      logger.info(`Scope ${scopeName} already exists`);
    } else {
      logger.info(`Scope ${appName} does not exist, creating it`);
      // 3. setup secrets in databricks
      await setupDatabricksAppSecrets({
        scopeName,
        databricksHost: currentApp.databricksHost,
        databricksApiKey: currentApp.databricksApiKey,
        secrets: {
          [DATABASE_URL_ENV_KEY]: connectionString,
        },
      });
    }

    // 4. Create a databricks config file, so the app can access Databricks secrets
    const databricksConfigFile = createDatabricksConfigFile([
      {
        sectionName: DATABASE_URL_RESOURCE_KEY,
        secretSection: {
          scope: scopeName,
          key: DATABASE_URL_ENV_KEY,
        },
      },
    ]);
    fs.writeFileSync(`${appDirectory}/databricks.yml`, databricksConfigFile);

    // 5. Create a databricks app file, to define the app's environment variables
    const databricksAppFile = createDatabricksAppFile([
      {
        name: DATABASE_URL_ENV_KEY,
        value: connectionString,
        isSecret: false,

        // TODO: use this instead, when we figure out how to get the secret value from the scope
        // value: DATABASE_URL_ENV_KEY,
        // isSecret: true,
      },
    ]);
    fs.writeFileSync(`${appDirectory}/app.yaml`, databricksAppFile);

    // 6. Import the code into the databricks workspace (after all files are created)
    logger.info('Importing code to Databricks workspace', {
      appId,
      workspaceSourceCodePath,
      databricksHost: currentApp.databricksHost,
    });
    await exec(
      `databricks workspace import-dir --overwrite "${appDirectory}" "${workspaceSourceCodePath}"`,
      {
        cwd: appDirectory,
        env: databricksEnv,
      },
    );

    // 7. Deploy the app from there
    logger.info('Deploying app to Databricks');
    const deployResult = await exec(
      `databricks apps deploy ${appName} --source-code-path /Workspace${workspaceSourceCodePath}`,
      {
        cwd: appDirectory,
        env: databricksEnv,
      },
    );
    logger.info('Databricks deployment completed', {
      appId,
      deployOutput: deployResult.stdout,
    });

    const appUrl = (
      await exec(`databricks apps get ${appName} | jq -r '.url'`, {
        env: databricksEnv,
      })
    ).stdout.trim();

    // Update app status to deployed
    await db
      .update(apps)
      .set({
        deployStatus: DeployStatus.DEPLOYED,
        appUrl,
      })
      .where(eq(apps.id, appId));

    return {
      appURL: appUrl,
      deploymentId: `databricks-${appId}`,
      deployStatus: DeployStatus.DEPLOYED,
    };
  } catch (error) {
    logger.error('Databricks deployment failed', {
      appId,
      error: error instanceof Error ? error.message : String(error),
    });

    // Update app status to failed
    await db
      .update(apps)
      .set({
        deployStatus: DeployStatus.FAILED,
      })
      .where(eq(apps.id, appId));

    throw new Error(
      `Databricks deployment failed: ${
        error instanceof Error ? error.message : String(error)
      }`,
    );
  }
}

async function checkDatabricksAppExists({
  appName,
  databricksHost,
  databricksApiKey,
}: {
  appName: string;
  databricksHost: string;
  databricksApiKey: string;
}) {
  try {
    const result = await exec(
      `databricks apps list | grep ${appName} || true`,
      {
        env: {
          ...process.env,
          DATABRICKS_HOST: databricksHost,
          DATABRICKS_TOKEN: databricksApiKey,
        },
      },
    );

    if (result.stderr) {
      logger.warn('Databricks app check stderr output', {
        appName,
        stderr: result.stderr,
      });
    }

    const appExists = result.stdout.trim().length > 0;
    logger.info('Checked Databricks app existence', {
      appName,
      appExists,
    });

    return appExists;
  } catch (error) {
    logger.error('Failed to check Databricks app existence', {
      appName,
      error: error instanceof Error ? error.message : String(error),
    });
    return false;
  }
}

async function checkIfScopeAlreadyExists({
  scopeName,
  databricksHost,
  databricksApiKey,
  secretName,
}: {
  scopeName: string;
  databricksHost: string;
  databricksApiKey: string;
  secretName: string;
}) {
  try {
    const result = await exec(
      `databricks secrets get-secret ${scopeName} ${secretName}`,
      {
        env: {
          ...process.env,
          DATABRICKS_HOST: databricksHost,
          DATABRICKS_TOKEN: databricksApiKey,
        },
      },
    );

    const jsonResult = JSON.parse(result.stdout);
    return databricksScopeSchema.parse(jsonResult);
  } catch (error) {
    if (error instanceof Error && error.message.includes('does not exist')) {
      return undefined;
    }

    logger.error('Failed to check if scope already exists', {
      scopeName,
      secretName,
      error: error instanceof Error ? error.message : String(error),
    });

    return undefined;
  }
}

/**
 * Validates appId to prevent shell injection attacks
 * Only allows alphanumeric characters, hyphens, and underscores
 */
function validateAppId(appId: string): void {
  if (!appId || typeof appId !== 'string') {
    throw new Error('App ID is required and must be a string');
  }

  if (!/^[a-zA-Z0-9_-]+$/.test(appId)) {
    throw new Error(
      'Invalid app ID: only alphanumeric characters, hyphens, and underscores are allowed',
    );
  }

  if (appId.length > 50) {
    throw new Error('App ID must be 50 characters or less');
  }
}

async function setupDatabricksAppSecrets({
  scopeName,
  databricksHost,
  databricksApiKey,
  secrets,
}: {
  scopeName: string;
  databricksHost: string;
  databricksApiKey: string;
  secrets: Record<string, string>;
}) {
  // 1. Create a secret scope
  logger.info('Creating secret scope', {
    scopeName,
  });
  const createSecretScopeResult = await exec(
    `databricks secrets create-scope ${scopeName}`,
    {
      env: {
        ...process.env,
        DATABRICKS_HOST: databricksHost,
        DATABRICKS_TOKEN: databricksApiKey,
      },
    },
  );
  if (createSecretScopeResult.stderr) {
    logger.error('Failed to create secret scope', {
      scopeName,
      stderr: createSecretScopeResult.stderr,
    });
    throw new Error('Failed to create secret scope');
  }

  // 2. Add secrets to the scope
  logger.info('Adding secrets to scope', {
    scopeName,
  });
  const addSecretsResult = await Promise.all(
    Object.entries(secrets).map(async ([key, value]) => {
      const payload = JSON.stringify({
        scope: scopeName,
        key: key,
        string_value: value,
      });

      return await exec(`databricks secrets put-secret --json '${payload}'`, {
        env: {
          ...process.env,
          DATABRICKS_HOST: databricksHost,
          DATABRICKS_TOKEN: databricksApiKey,
        },
      });
    }),
  );
  if (addSecretsResult.some((result) => result.stderr)) {
    logger.error('Failed to add secrets to scope', {
      scopeName,
      stderr: addSecretsResult.map((result) => result.stderr).join('\n'),
    });
    throw new Error('Failed to add secrets to scope');
  }
}

/**
 * Creates a Databricks config file
 * @param secrets - The secrets to be set in the config file
 * @returns The Databricks config file string
 */
function createDatabricksConfigFile(
  secrets: Array<{
    sectionName: string;
    secretSection: {
      scope: string;
      key: string;
    };
  }>,
) {
  let databricksConfigFile = `resources:
  secrets:
    ${secrets
      .map(
        (secret) => `${secret.sectionName}:
      scope: ${secret.secretSection.scope}
      key: ${secret.secretSection.key}`,
      )
      .join('\n')}
    `;

  return databricksConfigFile;
}

/**
 * Creates a Databricks app file
 * @param envVars - The environment variables to be set in the app
 * @returns The Databricks app file string
 */
function createDatabricksAppFile(
  envVars: Array<{
    name: string;
    value: string;
    isSecret: boolean;
  }>,
) {
  let databricksAppFile = `env:
  ${envVars
    .map(
      (envVar) => `- name: ${envVar.name}
    ${envVar.isSecret ? 'valueFrom:' : 'value:'} ${envVar.value}`,
    )
    .join('\n')}
  `;

  return databricksAppFile;
}

import { and, eq, isNull } from 'drizzle-orm';
import type { FastifyReply, FastifyRequest } from 'fastify';
import { validate } from 'uuid';
import { apps, appPrompts, deployments, db } from '../db';
import {
  deleteKoyebService,
  deleteKoyebDomain,
  deleteKoyebApp,
  deleteKoyebOrganization,
  getOrganizationToken,
} from '../deploy/koyeb';
import { deleteNeonProject } from '../deploy/neon';
import { deleteECRImages, deleteECRRepository } from '../ecr';
import { DEFAULT_OWNER, GithubEntity } from '../github/entity';
import { archiveRepository } from '../github/delete-repository';
import { logger } from '../logger';
import { Instrumentation } from '../instrumentation';

export async function deleteApp(
  request: FastifyRequest,
  reply: FastifyReply,
): Promise<void> {
  const user = request.user;
  const { id } = request.params as { id: string };

  if (!validate(id)) {
    return reply.status(400).send({ error: 'Invalid app ID' });
  }

  try {
    const app = await db
      .select({
        id: apps.id,
        name: apps.name,
        ownerId: apps.ownerId,
        koyebAppId: apps.koyebAppId,
        koyebServiceId: apps.koyebServiceId,
        koyebDomainId: apps.koyebDomainId,
        neonProjectId: apps.neonProjectId,
        githubUsername: apps.githubUsername,
        repositoryUrl: apps.repositoryUrl,
        deletedAt: apps.deletedAt,
      })
      .from(apps)
      .where(and(eq(apps.id, id), eq(apps.ownerId, user.id)));

    if (!app || app.length === 0) {
      return reply.status(404).send({
        error: 'App not found',
      });
    }

    const appData = app[0]!;

    // Check if app is already deleted
    if (appData.deletedAt) {
      return reply.status(400).send({
        error: 'App is already deleted',
      });
    }

    // Get organization info and check remaining deployments (for Koyeb cleanup decisions)
    const userDeployments = await db
      .select({
        koyebOrgId: deployments.koyebOrgId,
        appId: deployments.appId,
      })
      .from(deployments)
      .where(
        and(eq(deployments.ownerId, user.id), isNull(deployments.deletedAt)),
      );

    const koyebOrgId = userDeployments[0]?.koyebOrgId;
    const remainingDeployments = userDeployments.filter((d) => d.appId !== id);
    const isLastActiveDeployment = remainingDeployments.length === 0;

    await db.delete(appPrompts).where(eq(appPrompts.appId, id));
    logger.info('Hard deleted app prompts', { appId: id });

    await db
      .update(deployments)
      .set({ deletedAt: new Date() })
      .where(eq(deployments.appId, id));
    logger.info('Soft deleted deployment records', { appId: id });

    // Phase 2: Soft delete the main app record (preserve for analytics)
    await db
      .update(apps)
      .set({
        deletedAt: new Date(),
        updatedAt: new Date(),
        koyebAppId: null,
        koyebServiceId: null,
        koyebDomainId: null,
        neonProjectId: null,
      })
      .where(eq(apps.id, id));
    logger.info('Soft deleted main app record', { appId: id });

    await cleanupKoyebResources({
      appId: id,
      ownerId: user.id,
      koyebServiceId: appData.koyebServiceId,
      koyebDomainId: appData.koyebDomainId,
      koyebAppId: appData.koyebAppId,
      koyebOrgId: koyebOrgId,
      isLastActiveDeployment: isLastActiveDeployment,
    });

    await cleanupNeonResources({
      appId: id,
      neonProjectId: appData.neonProjectId,
    });

    await cleanupECRResources({
      appId: id,
      githubUsername: appData.githubUsername,
    });

    await cleanupGithubResources({
      appId: id,
      repositoryUrl: appData.repositoryUrl,
      githubUsername: appData.githubUsername,
      githubAccessToken: user.githubAccessToken,
    });

    return reply.status(200).send({
      message: 'App deleted successfully',
      appId: id,
    });
  } catch (error) {
    logger.error('Error deleting app', {
      appId: id,
      userId: user.id,
      error: error instanceof Error ? error.message : error,
    });

    Instrumentation.captureError(error as Error, {
      context: 'delete_app_main',
      appId: id,
      userId: user.id,
    });

    return reply.status(500).send({
      error: 'Internal server error while deleting app',
    });
  }
}

async function cleanupKoyebResources({
  appId,
  ownerId,
  koyebServiceId,
  koyebDomainId,
  koyebAppId,
  koyebOrgId,
  isLastActiveDeployment,
}: {
  appId: string;
  ownerId: string;
  koyebServiceId: string | null;
  koyebDomainId: string | null;
  koyebAppId: string | null;
  koyebOrgId: string | null | undefined;
  isLastActiveDeployment: boolean;
}) {
  // Skip if no Koyeb resources to clean up
  if (!koyebServiceId && !koyebDomainId && !koyebAppId) {
    logger.info('No Koyeb resources to clean up', { appId });
    return;
  }

  try {
    if (!koyebOrgId) {
      logger.warn('No Koyeb organization found for user, skipping cleanup', {
        appId,
        ownerId,
      });
      return;
    }

    const userToken = await getOrganizationToken(koyebOrgId);

    logger.info('Starting Koyeb resource cleanup', {
      appId,
      hasService: !!koyebServiceId,
      hasDomain: !!koyebDomainId,
      hasApp: !!koyebAppId,
    });

    if (koyebServiceId) {
      try {
        await deleteKoyebService({
          serviceId: koyebServiceId,
          token: userToken,
        });
        logger.info('Deleted Koyeb service', {
          appId,
          serviceId: koyebServiceId,
        });
      } catch (error) {
        logger.error('Failed to delete Koyeb service (continuing)', {
          appId,
          serviceId: koyebServiceId,
          error: error instanceof Error ? error.message : error,
        });

        Instrumentation.captureError(error as Error, {
          context: 'delete_koyeb_service',
          appId,
          serviceId: koyebServiceId,
        });
      }
    }

    if (koyebDomainId) {
      try {
        await deleteKoyebDomain({
          domainId: koyebDomainId,
          token: userToken,
        });
        logger.info('Deleted Koyeb domain', {
          appId,
          domainId: koyebDomainId,
        });
      } catch (error) {
        logger.error('Failed to delete Koyeb domain (continuing)', {
          appId,
          domainId: koyebDomainId,
          error: error instanceof Error ? error.message : error,
        });

        Instrumentation.captureError(error as Error, {
          context: 'delete_koyeb_domain',
          appId,
          domainId: koyebDomainId,
        });
      }
    }

    if (koyebAppId) {
      try {
        await deleteKoyebApp({
          appId: koyebAppId,
          token: userToken,
        });
        logger.info('Deleted Koyeb app', {
          appId,
          koyebAppId: koyebAppId,
        });
      } catch (error) {
        logger.error('Failed to delete Koyeb app (continuing)', {
          appId,
          koyebAppId: koyebAppId,
          error: error instanceof Error ? error.message : error,
        });

        Instrumentation.captureError(error as Error, {
          context: 'delete_koyeb_app',
          appId,
          koyebAppId: koyebAppId,
        });
      }
    }

    if (isLastActiveDeployment && koyebOrgId) {
      logger.info(
        'Initiating async Koyeb organization deletion (last active deployment)',
        {
          appId,
          koyebOrgId,
          ownerId,
        },
      );

      deleteKoyebOrganization({
        orgId: koyebOrgId,
        token: userToken,
      }).catch((error) => {
        logger.error('Async Koyeb organization deletion failed', {
          appId,
          koyebOrgId,
          error: error instanceof Error ? error.message : error,
        });
        Instrumentation.captureError(error as Error, {
          context: 'async_delete_koyeb_organization',
          appId,
          koyebOrgId,
        });
      });
    } else {
      logger.info('Keeping Koyeb organization (user has other deployments)', {
        appId,
        koyebOrgId,
        isLastActiveDeployment,
      });
    }

    logger.info('Koyeb resource cleanup completed', { appId });
  } catch (error) {
    logger.error('Failed to clean up Koyeb resources', {
      appId,
      ownerId,
      error: error instanceof Error ? error.message : error,
    });

    Instrumentation.captureError(error as Error, {
      context: 'cleanup_koyeb_resources',
      appId,
      ownerId,
    });
  }
}

async function cleanupNeonResources({
  appId,
  neonProjectId,
}: {
  appId: string;
  neonProjectId: string | null;
}) {
  if (!neonProjectId) {
    logger.info('No Neon project to clean up', { appId });
    return;
  }

  try {
    logger.info('Starting Neon project cleanup', {
      appId,
      neonProjectId,
    });

    await deleteNeonProject({
      projectId: neonProjectId,
    });

    logger.info('Neon project cleanup completed', {
      appId,
      neonProjectId,
    });
  } catch (error) {
    logger.error('Failed to clean up Neon project', {
      appId,
      neonProjectId,
      error: error instanceof Error ? error.message : error,
    });

    Instrumentation.captureError(error as Error, {
      context: 'cleanup_neon_resources',
      appId,
      neonProjectId,
    });
  }
}

async function cleanupECRResources({
  appId,
  githubUsername,
}: {
  appId: string;
  githubUsername: string | null;
}) {
  if (!githubUsername) {
    logger.info('No GitHub username available, skipping ECR cleanup', {
      appId,
    });
    return;
  }

  try {
    logger.info('Starting ECR resources cleanup', {
      appId,
      githubUsername,
    });

    await deleteECRImages({
      appId,
      githubUsername,
    });

    await deleteECRRepository({
      appId,
      githubUsername,
    });

    logger.info('ECR resources cleanup completed', {
      appId,
      githubUsername,
    });
  } catch (error) {
    logger.error('Failed to clean up ECR resources', {
      appId,
      githubUsername,
      error: error instanceof Error ? error.message : error,
    });

    Instrumentation.captureError(error as Error, {
      context: 'cleanup_ecr_resources',
      appId,
      githubUsername,
    });
  }
}

async function cleanupGithubResources({
  appId,
  repositoryUrl,
  githubUsername,
  githubAccessToken,
}: {
  appId: string;
  repositoryUrl: string | null;
  githubUsername: string | null;
  githubAccessToken: string;
}) {
  if (!repositoryUrl || !githubUsername) {
    logger.info(
      'No GitHub repository URL or username available, skipping GitHub cleanup',
      {
        appId,
        hasRepositoryUrl: !!repositoryUrl,
        hasGithubUsername: !!githubUsername,
      },
    );
    return;
  }

  try {
    // Parse repository URL to extract owner and repo name
    // Expected format: https://github.com/owner/repo
    const urlMatch = repositoryUrl.match(/github\.com\/([^\/]+)\/([^\/]+)/);
    if (!urlMatch) {
      logger.warn('Invalid GitHub repository URL format, skipping cleanup', {
        appId,
        repositoryUrl,
      });
      return;
    }

    const owner = urlMatch[1];
    const repo = urlMatch[2];

    if (!owner || !repo) {
      logger.warn(
        'Could not extract owner/repo from GitHub URL, skipping cleanup',
        {
          appId,
          repositoryUrl,
        },
      );
      return;
    }

    // Only archive repositories from the appdotbuilder organization
    if (owner !== DEFAULT_OWNER) {
      logger.info(
        `Repository is not from ${DEFAULT_OWNER} organization, skipping cleanup`,
        {
          appId,
          owner,
          repo,
        },
      );
      return;
    }

    logger.info('Starting GitHub repository cleanup', {
      appId,
      owner,
      repo,
      repositoryUrl,
    });

    const githubEntity = new GithubEntity(githubUsername, githubAccessToken);
    const initializedEntity = await githubEntity.init();
    initializedEntity.repo = repo;

    const result = await archiveRepository({
      githubEntity: initializedEntity,
    });

    if (result.statusCode === 200) {
      logger.info('GitHub repository archived successfully', {
        appId,
        owner,
        repo,
        result,
      });
    } else {
      logger.error('Failed to archive GitHub repository', {
        appId,
        owner,
        repo,
        error: result.error,
      });
    }

    logger.info('GitHub repository cleanup completed', {
      appId,
      owner,
      repo,
    });
  } catch (error) {
    logger.error('Failed to clean up GitHub repository', {
      appId,
      repositoryUrl,
      error: error instanceof Error ? error.message : error,
    });

    Instrumentation.captureError(error as Error, {
      context: 'cleanup_github_resources',
      appId,
      repositoryUrl,
    });
  }
}

