import { describe, it, expect, beforeEach, mock } from 'bun:test';
import { exec } from 'node:child_process';
import { promisify } from 'node:util';
import {
  deleteDatabricksApp,
  deleteDatabricksSecretScope,
  cleanupDatabricksResources,
} from './databricks';

// Mock dependencies
const mockExec = mock();
const mockLogger = {
  info: mock(),
  error: mock(),
  warn: mock(),
};

// Mock the exec and logger modules
mock.module('node:child_process', () => ({
  exec: mockExec,
}));
mock.module('../logger', () => ({
  logger: mockLogger,
}));

// Mock promisify to return our mocked exec
const mockedPromisifyExec = mock();
mock.module('node:util', () => ({
  promisify: () => mockedPromisifyExec,
}));

describe('Databricks Cleanup Functions', () => {
  beforeEach(() => {
    // Clear all mocks before each test
    mockExec.mockClear();
    mockLogger.info.mockClear();
    mockLogger.error.mockClear();
    mockLogger.warn.mockClear();
    mockedPromisifyExec.mockClear();
  });

  describe('deleteDatabricksApp', () => {
    it('should delete existing app successfully', async () => {
      // Mock app exists check (returns app name)
      mockedPromisifyExec
        .mockResolvedValueOnce({ stdout: 'app-12345678', stderr: '' })
        .mockResolvedValueOnce({ stdout: '', stderr: '' });

      await deleteDatabricksApp({
        appName: 'app-12345678',
        databricksHost: 'https://test.databricks.com',
        databricksApiKey: 'test-key',
      });

      // Verify the delete command was called
      expect(mockedPromisifyExec).toHaveBeenCalledWith(
        'databricks apps delete app-12345678',
        expect.objectContaining({
          env: expect.objectContaining({
            DATABRICKS_HOST: 'https://test.databricks.com',
            DATABRICKS_TOKEN: 'test-key',
          }),
        }),
      );

      // Verify logging
      expect(mockLogger.info).toHaveBeenCalledWith(
        'Deleting Databricks app: app-12345678',
      );
      expect(mockLogger.info).toHaveBeenCalledWith(
        'Successfully deleted Databricks app: app-12345678',
      );
    });

    it('should handle non-existent app gracefully', async () => {
      // Mock app does not exist (empty stdout)
      mockedPromisifyExec.mockResolvedValueOnce({ stdout: '', stderr: '' });

      await deleteDatabricksApp({
        appName: 'non-existent-app',
        databricksHost: 'https://test.databricks.com',
        databricksApiKey: 'test-key',
      });

      // Should only call the existence check, not the delete command
      expect(mockedPromisifyExec).toHaveBeenCalledTimes(1);

      // Verify logging
      expect(mockLogger.info).toHaveBeenCalledWith(
        'Databricks app non-existent-app does not exist, skipping deletion',
      );
    });

    it('should handle errors and rethrow them', async () => {
      // Mock app exists
      mockedPromisifyExec
        .mockResolvedValueOnce({ stdout: 'app-12345678', stderr: '' })
        .mockRejectedValueOnce(new Error('Databricks CLI error'));

      await expect(
        deleteDatabricksApp({
          appName: 'app-12345678',
          databricksHost: 'https://test.databricks.com',
          databricksApiKey: 'test-key',
        }),
      ).rejects.toThrow('Databricks CLI error');

      // Verify error was logged
      expect(mockLogger.error).toHaveBeenCalledWith(
        'Failed to delete Databricks app app-12345678',
        expect.objectContaining({
          error: 'Databricks CLI error',
        }),
      );
    });
  });

  describe('deleteDatabricksSecretScope', () => {
    it('should delete existing scope successfully', async () => {
      // Mock scope exists check (returns valid JSON)
      mockedPromisifyExec
        .mockResolvedValueOnce({
          stdout: JSON.stringify({ key: 'APP_DATABASE_URL', value: 'test' }),
          stderr: '',
        })
        .mockResolvedValueOnce({ stdout: '', stderr: '' });

      await deleteDatabricksSecretScope({
        scopeName: 'test-scope',
        databricksHost: 'https://test.databricks.com',
        databricksApiKey: 'test-key',
      });

      // Verify the delete command was called
      expect(mockedPromisifyExec).toHaveBeenCalledWith(
        'databricks secrets delete-scope --scope test-scope',
        expect.objectContaining({
          env: expect.objectContaining({
            DATABRICKS_HOST: 'https://test.databricks.com',
            DATABRICKS_TOKEN: 'test-key',
          }),
        }),
      );

      // Verify logging
      expect(mockLogger.info).toHaveBeenCalledWith(
        'Deleting Databricks secret scope: test-scope',
      );
      expect(mockLogger.info).toHaveBeenCalledWith(
        'Successfully deleted Databricks scope: test-scope',
      );
    });

    it('should handle non-existent scope gracefully', async () => {
      // Mock scope does not exist (error contains "does not exist")
      mockedPromisifyExec.mockRejectedValueOnce(
        new Error('Scope test-scope does not exist'),
      );

      await deleteDatabricksSecretScope({
        scopeName: 'non-existent-scope',
        databricksHost: 'https://test.databricks.com',
        databricksApiKey: 'test-key',
      });

      // Should only call the existence check, not the delete command
      expect(mockedPromisifyExec).toHaveBeenCalledTimes(1);

      // Verify logging
      expect(mockLogger.info).toHaveBeenCalledWith(
        'Databricks scope non-existent-scope does not exist, skipping deletion',
      );
    });
  });

  describe('cleanupDatabricksResources', () => {
    it('should cleanup both app and scope successfully', async () => {
      // Mock app exists and scope exists
      mockedPromisifyExec
        .mockResolvedValueOnce({ stdout: 'app-12345678', stderr: '' }) // app exists check
        .mockResolvedValueOnce({ stdout: '', stderr: '' }) // app delete
        .mockResolvedValueOnce({
          stdout: JSON.stringify({ key: 'APP_DATABASE_URL', value: 'test' }),
          stderr: '',
        }) // scope exists check
        .mockResolvedValueOnce({ stdout: '', stderr: '' }); // scope delete

      await cleanupDatabricksResources({
        appId: '12345678-1234-1234-1234-123456789012',
        databricksHost: 'https://test.databricks.com',
        databricksApiKey: 'test-key',
      });

      // Verify the cleanup process was logged
      expect(mockLogger.info).toHaveBeenCalledWith(
        'Starting Databricks cleanup for app: app-12345678',
        expect.objectContaining({
          appId: '12345678-1234-1234-1234-123456789012',
          databricksHost: 'https://test.databricks.com',
        }),
      );

      expect(mockLogger.info).toHaveBeenCalledWith(
        'Completed Databricks cleanup for app: app-12345678',
      );
    });

    it('should handle cleanup errors and rethrow them', async () => {
      // Mock app exists but delete fails
      mockedPromisifyExec
        .mockResolvedValueOnce({ stdout: 'app-12345678', stderr: '' })
        .mockRejectedValueOnce(new Error('Delete failed'));

      await expect(
        cleanupDatabricksResources({
          appId: '12345678-1234-1234-1234-123456789012',
          databricksHost: 'https://test.databricks.com',
          databricksApiKey: 'test-key',
        }),
      ).rejects.toThrow('Delete failed');

      // Verify error was logged
      expect(mockLogger.error).toHaveBeenCalledWith(
        'Databricks cleanup failed for app: app-12345678',
        expect.objectContaining({
          appId: '12345678-1234-1234-1234-123456789012',
          error: 'Delete failed',
        }),
      );
    });

    it('should validate appId and throw on invalid input', async () => {
      await expect(
        cleanupDatabricksResources({
          appId: 'invalid-app-id-with-special-chars!',
          databricksHost: 'https://test.databricks.com',
          databricksApiKey: 'test-key',
        }),
      ).rejects.toThrow('Invalid app ID');
    });
  });
});

