      appId: app.id,
      hasHost: !!app.databricksHost,
      hasApiKey: !!app.databricksApiKey 
    });
  } else {
    try {
      await cleanupDatabricksResources({
        appId: app.id,
        databricksHost: app.databricksHost,
        databricksApiKey: app.databricksApiKey,
      });
      logger.info('Databricks resource cleanup completed', { appId: app.id });
    } catch (error) {
      logger.error('Failed to cleanup Databricks resources', {
        appId: app.id,
        error: error instanceof Error ? error.message : String(error),
      });
      // Continue with deletion even if Databricks cleanup fails
    }
  }
}
```

