import { Badge } from '@appdotbuild/design';
import { Server, Database, Check } from 'lucide-react';
import { useUser } from '@stackframe/react';
import type { DeploymentTarget, DatabricksConfig } from '~/types/deployment';
import { DatabricksConfigForm } from './databricks-config-form';
import { cn } from '~/lib/utils';

type DeploymentTargetSelectorProps = {
  selectedTarget: DeploymentTarget;
  onTargetChange: (target: DeploymentTarget) => void;
  databricksConfig: DatabricksConfig;
  onDatabricksConfigChange: (config: DatabricksConfig) => void;
  className?: string;
};

export function DeploymentTargetSelector({
  selectedTarget,
  onTargetChange,
  databricksConfig,
  onDatabricksConfigChange,
  className,
}: DeploymentTargetSelectorProps) {
  const user = useUser();
  const isStaff = user?.clientReadOnlyMetadata?.role === 'staff';

  // Don't render for non-staff users
  if (!isStaff) {
    return null;
  }

  return (
    <div className={cn('space-y-5', className)}>
      <div className="space-y-4">
        <div className="flex items-center gap-2">
          <div className="text-sm font-semibold text-slate-800 dark:text-slate-200 tracking-tight">
            Deployment Platform
          </div>
          <div className="text-xs px-2 py-0.5 rounded-md bg-slate-100 dark:bg-slate-700 text-slate-600 dark:text-slate-400 font-medium">
            Staff Only
          </div>
        </div>

        <div className="flex items-stretch gap-3">
          {/* Cloud Platform Option */}
          <button
            onClick={() => onTargetChange('koyeb')}
            className={cn(
              'group relative flex items-center gap-4 px-5 py-4 rounded-xl text-sm transition-all duration-200',
              'border bg-white/80 dark:bg-slate-800/60 hover:shadow-md backdrop-blur-sm',
              'focus:outline-none focus:ring-2 focus:ring-blue-500/25 focus:border-blue-300',
              'flex-1 min-h-[80px]',
              selectedTarget === 'koyeb'
                ? 'border-blue-300/70 bg-blue-50/90 dark:border-blue-400/40 dark:bg-blue-900/30 shadow-lg shadow-blue-500/10'
                : 'border-slate-200/70 dark:border-slate-600/50 hover:border-slate-300 dark:hover:border-slate-500',
            )}
          >
            {/* Selection Indicator */}
            <div
              className={cn(
                'w-4 h-4 rounded-full border-2 flex items-center justify-center transition-colors',
                selectedTarget === 'koyeb'
                  ? 'border-blue-500 bg-blue-500'
                  : 'border-slate-300 dark:border-slate-600 group-hover:border-slate-400',
              )}
            >
              {selectedTarget === 'koyeb' && (
                <Check className="w-2.5 h-2.5 text-white" strokeWidth={3} />
              )}
            </div>

            {/* Icon */}
            <div
              className={cn(
                'p-2.5 rounded-lg transition-colors shadow-sm',
                selectedTarget === 'koyeb'
                  ? 'bg-gradient-to-br from-blue-100 to-blue-200/80 dark:from-blue-900/60 dark:to-blue-800/40 text-blue-600 dark:text-blue-400'
                  : 'bg-gradient-to-br from-slate-100 to-slate-200/60 dark:from-slate-700 dark:to-slate-600/60 text-slate-600 dark:text-slate-400 group-hover:from-slate-200 group-hover:to-slate-300/80',
              )}
            >
              <Server className="w-4 h-4" />
            </div>

            {/* Content */}
            <div className="flex-1 text-left">
              <div
                className={cn(
                  'font-medium transition-colors',
                  selectedTarget === 'koyeb'
                    ? 'text-blue-900 dark:text-blue-100'
                    : 'text-slate-700 dark:text-slate-200 group-hover:text-slate-900 dark:group-hover:text-slate-100',
                )}
              >
                Koyeb
              </div>
              <div
                className={cn(
                  'text-xs mt-0.5 transition-colors',
                  selectedTarget === 'koyeb'
                    ? 'text-blue-600 dark:text-blue-300'
                    : 'text-slate-500 dark:text-slate-400',
                )}
              >
                Cloud Platform
              </div>
            </div>
          </button>

          {/* Enterprise Option */}
          <button
            onClick={() => onTargetChange('databricks')}
            className={cn(
              'group relative flex items-center gap-4 px-5 py-4 rounded-xl text-sm transition-all duration-200',
              'border bg-white/80 dark:bg-slate-800/60 hover:shadow-md backdrop-blur-sm',
              'focus:outline-none focus:ring-2 focus:ring-slate-500/25 focus:border-slate-400',
              'flex-1 min-h-[80px]',
              selectedTarget === 'databricks'
                ? 'border-slate-400/70 bg-slate-100/90 dark:border-slate-500/40 dark:bg-slate-700/30 shadow-lg shadow-slate-500/10'
                : 'border-slate-200/70 dark:border-slate-600/50 hover:border-slate-300 dark:hover:border-slate-500',
            )}
          >
            {/* Selection Indicator */}
            <div
              className={cn(
                'w-4 h-4 rounded-full border-2 flex items-center justify-center transition-colors',
                selectedTarget === 'databricks'
                  ? 'border-slate-600 bg-slate-600'
                  : 'border-slate-300 dark:border-slate-600 group-hover:border-slate-400',
              )}
            >
              {selectedTarget === 'databricks' && (
                <Check className="w-2.5 h-2.5 text-white" strokeWidth={3} />
              )}
            </div>

            {/* Icon */}
            <div
              className={cn(
                'p-2.5 rounded-lg transition-colors shadow-sm',
                selectedTarget === 'databricks'
                  ? 'bg-gradient-to-br from-slate-200 to-slate-300/80 dark:from-slate-600/60 dark:to-slate-500/40 text-slate-700 dark:text-slate-200'
                  : 'bg-gradient-to-br from-slate-100 to-slate-200/60 dark:from-slate-700 dark:to-slate-600/60 text-slate-600 dark:text-slate-400 group-hover:from-slate-200 group-hover:to-slate-300/80',
              )}
            >
              <Database className="w-4 h-4" />
            </div>

            {/* Content */}
            <div className="flex-1 text-left">
              <div className="flex items-center gap-2">
                <span
                  className={cn(
                    'font-medium transition-colors',
                    selectedTarget === 'databricks'
                      ? 'text-slate-800 dark:text-slate-100'
                      : 'text-slate-700 dark:text-slate-200 group-hover:text-slate-900 dark:group-hover:text-slate-100',
                  )}
                >
                  Databricks Apps
                </span>
                <Badge
                  variant="secondary"
                  className={cn(
                    'text-xs px-2 py-0.5 font-medium transition-colors',
                    selectedTarget === 'databricks'
                      ? 'bg-slate-200 text-slate-700 border-slate-300 dark:bg-slate-600/50 dark:text-slate-200 dark:border-slate-500'
                      : 'bg-slate-100 text-slate-600 border-slate-200 dark:bg-slate-700 dark:text-slate-400 dark:border-slate-600',
                  )}
                >
                  Advanced
                </Badge>
              </div>
            </div>
          </button>
        </div>

        {/* Enterprise Configuration Form */}
        {selectedTarget === 'databricks' && (
          <div className="mt-6 p-6 border border-slate-200 dark:border-slate-600 rounded-xl bg-gradient-to-br from-slate-50/60 to-slate-100/30 dark:from-slate-800/20 dark:to-slate-700/10 backdrop-blur-sm shadow-sm">
            <div className="mb-5">
              <h3 className="text-sm font-bold text-slate-800 dark:text-slate-200 tracking-tight">
                Workspace Configuration
              </h3>
              <p className="text-xs text-slate-600 dark:text-slate-400 mt-1.5 leading-relaxed">
                Connect your Databricks workspace to deploy apps
              </p>
            </div>
            <DatabricksConfigForm
              config={databricksConfig}
              onConfigChange={onDatabricksConfigChange}
            />
          </div>
        )}

        {/* Contextual help text */}
        <div
          className={cn(
            'text-xs transition-colors leading-relaxed px-1',
            selectedTarget === 'koyeb'
              ? 'text-blue-600/80 dark:text-blue-400/80'
              : 'text-slate-600 dark:text-slate-400',
          )}
        >
          {selectedTarget === 'koyeb' ? (
            <span>
              Fast, reliable deployment to our managed cloud infrastructure
            </span>
          ) : (
            <span>
              Deploy to Databricks Apps. Currently supports NiceGUI applications
              only.
            </span>
          )}
        </div>
      </div>
    </div>
  );
}

