import { Input, Label } from '@appdotbuild/design';
import { Link2, Key } from 'lucide-react';
import type { DatabricksConfig } from '~/types/deployment';
import { cn } from '~/lib/utils';

type DatabricksConfigFormProps = {
  config: DatabricksConfig;
  onConfigChange: (config: DatabricksConfig) => void;
  className?: string;
};

export function DatabricksConfigForm({
  config,
  onConfigChange,
  className,
}: DatabricksConfigFormProps) {
  const handleOrgDetail1Change = (e: React.ChangeEvent<HTMLInputElement>) => {
    onConfigChange({
      ...config,
      orgDetail1: e.target.value,
    });
  };

  const handleOrgDetail2Change = (e: React.ChangeEvent<HTMLInputElement>) => {
    onConfigChange({
      ...config,
      orgDetail2: e.target.value,
    });
  };

  return (
    <div className={className}>
      <div className="space-y-6">
        <div className="space-y-3">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-gradient-to-br from-slate-100 to-slate-200/80 dark:from-slate-700/60 dark:to-slate-600/40 shadow-sm">
              <Link2 className="w-4 h-4 text-slate-600 dark:text-slate-300" />
            </div>
            <Label
              htmlFor="databricks-org-detail-1"
              className="text-sm font-semibold text-slate-800 dark:text-slate-200"
            >
              Workspace URL
            </Label>
          </div>
          <Input
            id="databricks-org-detail-1"
            type="url"
            placeholder="https://workspace.cloud.databricks.com"
            value={config.orgDetail1}
            onChange={handleOrgDetail1Change}
            className={cn(
              'h-10 text-sm transition-all duration-200',
              'border-slate-200 dark:border-slate-600',
              'focus:border-slate-400 focus:ring-2 focus:ring-slate-500/20',
              'bg-white dark:bg-slate-800/50',
              'placeholder:text-slate-400 dark:placeholder:text-slate-500',
              'shadow-sm hover:shadow focus:shadow-md',
              'backdrop-blur-sm',
            )}
          />
          <p className="text-xs text-slate-600 dark:text-slate-400 leading-relaxed">
            Your Databricks workspace URL
          </p>
        </div>

        <div className="space-y-3">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-gradient-to-br from-slate-100 to-slate-200/80 dark:from-slate-700/60 dark:to-slate-600/40 shadow-sm">
              <Key className="w-4 h-4 text-slate-600 dark:text-slate-300" />
            </div>
            <Label
              htmlFor="databricks-org-detail-2"
              className="text-sm font-semibold text-slate-800 dark:text-slate-200"
            >
              Access Token
            </Label>
          </div>
          <Input
            id="databricks-org-detail-2"
            type="password"
            placeholder="dapi••••••••••••••••••••"
            value={config.orgDetail2}
            onChange={handleOrgDetail2Change}
            className={cn(
              'h-10 text-sm transition-all duration-200',
              'border-slate-200 dark:border-slate-600',
              'focus:border-slate-400 focus:ring-2 focus:ring-slate-500/20',
              'bg-white dark:bg-slate-800/50',
              'placeholder:text-slate-400 dark:placeholder:text-slate-500',
              'shadow-sm hover:shadow focus:shadow-md',
              'backdrop-blur-sm',
            )}
          />
          <p className="text-xs text-slate-600 dark:text-slate-400 leading-relaxed">
            Generate from user settings → Access tokens
          </p>
        </div>
      </div>
    </div>
  );
}

