import {
  Badge,
  Card,
  CardContent,
  CardHeader,
  CardTitle,
  CardDescription,
} from '@appdotbuild/design';
import { Server, Database, Check } from 'lucide-react';
import { useUser } from '@stackframe/react';
import type { DeploymentTarget, DatabricksConfig } from '~/types/deployment';
import { DatabricksConfigForm } from './databricks-config-form';
import { cn } from '~/lib/utils';

type DeploymentTargetSelectorProps = {
  selectedTarget: DeploymentTarget;
  onTargetChange: (target: DeploymentTarget) => void;
  databricksConfig: DatabricksConfig;
  onDatabricksConfigChange: (config: DatabricksConfig) => void;
  className?: string;
};

export function DeploymentTargetSelector({
  selectedTarget,
  onTargetChange,
  databricksConfig,
  onDatabricksConfigChange,
  className,
}: DeploymentTargetSelectorProps) {
  const user = useUser();
  const isStaff = user?.clientReadOnlyMetadata?.role === 'staff';

  // Don't render for non-staff users
  if (!isStaff) {
    return null;
  }

  return (
    <div className={cn('space-y-6', className)}>
      <div className="flex items-center gap-2">
        <h3 className="text-sm font-semibold">Deployment Platform</h3>
        <Badge variant="secondary" className="text-xs">
          Staff Only
        </Badge>
      </div>

      <div className="grid gap-4 md:grid-cols-2">
        <Card
          className={cn(
            'cursor-pointer transition-all hover:shadow-md',
            selectedTarget === 'koyeb'
              ? 'ring-2 ring-primary border-primary bg-primary/5'
              : 'hover:border-primary/50',
          )}
        >
          <CardContent className="p-6" onClick={() => onTargetChange('koyeb')}>
            <div className="flex items-start gap-4">
              <div className="flex-shrink-0 mt-1">
                <div
                  className={cn(
                    'w-4 h-4 rounded-full border-2 flex items-center justify-center',
                    selectedTarget === 'koyeb'
                      ? 'border-primary bg-primary'
                      : 'border-muted-foreground',
                  )}
                >
                  {selectedTarget === 'koyeb' && (
                    <Check
                      className="w-2.5 h-2.5 text-primary-foreground"
                      strokeWidth={3}
                    />
                  )}
                </div>
              </div>
              <div className="flex-1 space-y-2">
                <div className="flex items-center gap-3">
                  <div className="flex-shrink-0 p-2 bg-muted rounded-md">
                    <Server className="w-4 h-4" />
                  </div>
                  <div>
                    <h4 className="font-medium">Koyeb</h4>
                    <p className="text-sm text-muted-foreground">
                      Cloud Platform
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card
          className={cn(
            'cursor-pointer transition-all hover:shadow-md',
            selectedTarget === 'databricks'
              ? 'ring-2 ring-primary border-primary bg-primary/5'
              : 'hover:border-primary/50',
          )}
        >
          <CardContent
            className="p-6"
            onClick={() => onTargetChange('databricks')}
          >
            <div className="flex items-start gap-4">
              <div className="flex-shrink-0 mt-1">
                <div
                  className={cn(
                    'w-4 h-4 rounded-full border-2 flex items-center justify-center',
                    selectedTarget === 'databricks'
                      ? 'border-primary bg-primary'
                      : 'border-muted-foreground',
                  )}
                >
                  {selectedTarget === 'databricks' && (
                    <Check
                      className="w-2.5 h-2.5 text-primary-foreground"
                      strokeWidth={3}
                    />
                  )}
                </div>
              </div>
              <div className="flex-1 space-y-2">
                <div className="flex items-center gap-3">
                  <div className="flex-shrink-0 p-2 bg-muted rounded-md">
                    <Database className="w-4 h-4" />
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center gap-2">
                      <h4 className="font-medium">Databricks Apps</h4>
                      <Badge variant="outline" className="text-xs">
                        Advanced
                      </Badge>
                    </div>
                    <p className="text-sm text-muted-foreground">
                      Enterprise Platform
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {selectedTarget === 'databricks' && (
        <Card>
          <CardHeader>
            <CardTitle>Workspace Configuration</CardTitle>
            <CardDescription>
              Connect your Databricks workspace to deploy apps
            </CardDescription>
          </CardHeader>
          <CardContent>
            <DatabricksConfigForm
              config={databricksConfig}
              onConfigChange={onDatabricksConfigChange}
            />
          </CardContent>
        </Card>
      )}

      <p className="text-xs text-muted-foreground">
        {selectedTarget === 'koyeb'
          ? 'Fast, reliable deployment to our managed cloud infrastructure'
          : 'Deploy to Databricks Apps. Currently supports NiceGUI applications only.'}
      </p>
    </div>
  );
}

