import {
  Badge,
  Card,
  CardContent,
  CardHeader,
  CardTitle,
  CardDescription,
} from '@appdotbuild/design';
import { Server, Database } from 'lucide-react';
import { useUser } from '@stackframe/react';
import type { DeploymentTarget, DatabricksConfig } from '~/types/deployment';
import { DatabricksConfigForm } from './databricks-config-form';
import { cn } from '~/lib/utils';

type DeploymentTargetSelectorProps = {
  selectedTarget: DeploymentTarget;
  onTargetChange: (target: DeploymentTarget) => void;
  databricksConfig: DatabricksConfig;
  onDatabricksConfigChange: (config: DatabricksConfig) => void;
  className?: string;
};

export function DeploymentTargetSelector({
  selectedTarget,
  onTargetChange,
  databricksConfig,
  onDatabricksConfigChange,
  className,
}: DeploymentTargetSelectorProps) {
  const user = useUser();
  const isStaff = user?.clientReadOnlyMetadata?.role === 'staff';

  // Don't render for non-staff users
  if (!isStaff) {
    return null;
  }

  return (
    <div className={cn('space-y-6', className)}>
      <div className="flex items-center gap-2">
        <h3 className="text-sm font-semibold">Deployment Platform</h3>
        <Badge variant="outline" className="text-xs">
          Staff Only
        </Badge>
      </div>

      <div className="grid gap-4 md:grid-cols-2">
        {/* Koyeb Card */}
        <Card
          className={cn(
            'relative cursor-pointer transition-all duration-200 hover:bg-muted/50',
            selectedTarget === 'koyeb'
              ? 'border-2 border-primary bg-primary/10'
              : 'border border-input',
          )}
          onClick={() => onTargetChange('koyeb')}
        >
          <CardContent className="p-6">
            <div className="flex items-center gap-4">
              <div className="flex-shrink-0 p-3 bg-muted/50 rounded-lg">
                <Server
                  className={cn(
                    'w-6 h-6 transition-colors',
                    selectedTarget === 'koyeb'
                      ? 'text-primary'
                      : 'text-muted-foreground',
                  )}
                />
              </div>
              <div className="flex-1">
                <h4 className="font-semibold text-base mb-1">Koyeb</h4>
                <p className="text-sm text-muted-foreground">
                  Default Cloud Platform
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Databricks Card */}
        <Card
          className={cn(
            'relative cursor-pointer transition-all duration-200 hover:bg-muted/50',
            selectedTarget === 'databricks'
              ? 'border-2 border-primary bg-primary/10'
              : 'border border-input',
          )}
          onClick={() => onTargetChange('databricks')}
        >
          <CardContent className="p-6">
            <div className="flex items-center gap-4">
              <div className="flex-shrink-0 p-3 bg-muted/50 rounded-lg">
                <Database
                  className={cn(
                    'w-6 h-6 transition-colors',
                    selectedTarget === 'databricks'
                      ? 'text-primary'
                      : 'text-muted-foreground',
                  )}
                />
              </div>
              <div className="flex-1">
                <div className="flex items-center gap-2 mb-1">
                  <h4 className="font-semibold text-base">Databricks Apps</h4>
                </div>
                <p className="text-sm text-muted-foreground">
                  Enterprise Platform
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {selectedTarget === 'databricks' && (
        <Card className="animate-in slide-in-from-top-2 duration-300">
          <CardHeader className="pb-4">
            <CardTitle className="text-lg">Workspace Configuration</CardTitle>
            <CardDescription>
              Connect your Databricks workspace to deploy apps
            </CardDescription>
          </CardHeader>
          <CardContent>
            <DatabricksConfigForm
              config={databricksConfig}
              onConfigChange={onDatabricksConfigChange}
            />
          </CardContent>
        </Card>
      )}

      <div className="text-center">
        <p className="text-xs text-muted-foreground">
          {selectedTarget === 'koyeb'
            ? 'Fast, reliable deployment to our managed cloud infrastructure'
            : 'Deploy to Databricks Apps. Currently supports NiceGUI applications only.'}
        </p>
      </div>
    </div>
  );
}

