1:import {
2:  Badge,
3:  Card,
4:  CardContent,
5:  CardHeader,
6:  CardTitle,
7:  CardDescription,
8:} from '@appdotbuild/design';
9:import { Server, Database } from 'lucide-react';
10:import { DatabricksConfigForm } from './databricks-config-form';
11:import { cn } from '@appdotbuild/design';
12:import { useForm, FormProvider } from 'react-hook-form';
13:import { zodResolver } from '@hookform/resolvers/zod';
14:import { z } from 'zod';
15:import { forwardRef, useImperativeHandle } from 'react';
17:const KoyebSchema = z.object({
18:  selectedTarget: z.literal('koyeb'),
19:});
20:const DatabricksConfigSchema = z.object({
21:  hostUrl: z.string().min(1, 'Workspace URL is required'),
22:  personalAccessToken: z.string().min(1, 'Access token is required'),
23:});
24:const DatabricksSchema = z.object({
25:  selectedTarget: z.literal('databricks'),
26:  databricksConfig: DatabricksConfigSchema,
27:});
28:const deploymentFormSchema = z.union([KoyebSchema, DatabricksSchema]);
30:type DeploymentFormData = z.infer<typeof deploymentFormSchema>;
31:export type DeploymentTarget = DeploymentFormData['selectedTarget'];
32:export type DeploymentConfig = z.infer<typeof deploymentFormSchema>;
34:// Imperative handle interface
35:export interface DeploymentTargetSelectorHandle {
36:  getDeploymentConfig: () => DeploymentConfig;
37:  deployInformationIsValid: () => boolean;
38:}
40:type DeploymentTargetSelectorProps = {
41:  className?: string;
42:};
44:export const DeploymentTargetSelector = forwardRef<
45:  DeploymentTargetSelectorHandle,
46:  DeploymentTargetSelectorProps
47:>(({ className }, ref) => {
48:  const formMethods = useForm<DeploymentFormData>({
49:    resolver: zodResolver(deploymentFormSchema),
50:    defaultValues: {
51:      selectedTarget: 'koyeb',
52:    },
53:  });
55:  const { setValue, watch, getValues, formState } = formMethods;
56:  const watchedTarget = watch('selectedTarget');
58:  // Expose methods via imperative handle
59:  useImperativeHandle(
60:    ref,
61:    () => ({
62:      getDeploymentConfig: (): DeploymentConfig => {
63:        if (getValues('selectedTarget') === 'databricks') {
64:          return {
65:            selectedTarget: 'databricks',
66:            databricksConfig: getValues('databricksConfig'),
67:          };
68:        }
69:        return { selectedTarget: 'koyeb' };
70:      },
71:      deployInformationIsValid: (): boolean => {
72:        return formState.isValid;
73:      },
74:    }),
75:    [formState.isValid, getValues],
76:  );
78:  const handleTargetClick = (target: DeploymentTarget) => {
79:    setValue('selectedTarget', target);
80:  };
82:  return (
83:    <FormProvider {...formMethods}>
84:      <div className={cn('space-y-4', className)}>
85:        <div className="flex items-center gap-2">
86:          <h3 className="text-sm font-semibold">Deployment Platform</h3>
87:          <Badge variant="outline" className="text-xs">
88:            Staff Only
89:          </Badge>
90:        </div>
92:        <div className="grid gap-4 md:grid-cols-2">
93:          {/* Koyeb Card */}
94:          <Card
95:            className={cn(
96:              'relative cursor-pointer transition-all duration-200 hover:bg-muted/50',
97:              watchedTarget === 'koyeb'
98:                ? 'border-2 border-primary bg-primary/10'
99:                : 'border-2 border-transparent',
100:            )}
101:            onClick={() => handleTargetClick('koyeb')}
102:          >
103:            <CardContent className="p-6">
104:              <div className="flex items-center gap-4">
105:                <div className="flex-shrink-0 p-3 bg-muted/50 rounded-lg">
106:                  <Server
107:                    className={cn(
108:                      'w-6 h-6 transition-colors',
109:                      watchedTarget === 'koyeb'
110:                        ? 'text-primary'
111:                        : 'text-muted-foreground',
112:                    )}
113:                  />
114:                </div>
115:                <div className="flex-1">
116:                  <h4 className="font-semibold text-base mb-1">Koyeb</h4>
117:                  <p className="text-sm text-muted-foreground">
118:                    Default Cloud Platform
119:                  </p>
120:                </div>
121:              </div>
122:            </CardContent>
123:          </Card>
125:          {/* Databricks Card */}
126:          <Card
127:            className={cn(
128:              'relative cursor-pointer transition-all duration-200 hover:bg-muted/50',
129:              watchedTarget === 'databricks'
130:                ? 'border-2 border-primary bg-primary/10'
131:                : 'border-2 border-transparent',
132:            )}
133:            onClick={() => handleTargetClick('databricks')}
134:          >
135:            <CardContent className="p-6">
136:              <div className="flex items-center gap-4">
137:                <div className="flex-shrink-0 p-3 bg-muted/50 rounded-lg">
138:                  <Database
139:                    className={cn(
140:                      'w-6 h-6 transition-colors',
141:                      watchedTarget === 'databricks'
142:                        ? 'text-primary'
143:                        : 'text-muted-foreground',
144:                    )}
145:                  />
146:                </div>
147:                <div className="flex-1">
148:                  <div className="flex items-center gap-2 mb-1">
149:                    <h4 className="font-semibold text-base">Databricks Apps</h4>
150:                  </div>
151:                  <p className="text-sm text-muted-foreground">
152:                    Enterprise Platform
153:                  </p>
154:                </div>
155:              </div>
156:            </CardContent>
157:          </Card>
158:        </div>
160:        {watchedTarget === 'databricks' && (
161:          <Card className="animate-in slide-in-from-top-2 duration-300">
162:            <CardHeader className="pb-4">
163:              <CardTitle className="text-lg">Workspace Configuration</CardTitle>
164:              <CardDescription>
165:                Connect your Databricks workspace to deploy apps
166:              </CardDescription>
167:            </CardHeader>
168:            <CardContent>
169:              <DatabricksConfigForm />
170:            </CardContent>
171:          </Card>
172:        )}
174:        <div className="text-center">
175:          <p className="text-xs text-muted-foreground">
176:            {watchedTarget === 'koyeb'
177:              ? 'Fast, reliable deployment to our managed cloud infrastructure'
178:              : 'Deploy to Databricks Apps. Currently supports NiceGUI applications only.'}
179:          </p>
180:        </div>
181:      </div>
182:    </FormProvider>
183:  );
184:});

