1:import { Box, Text } from 'ink';
2:import TextInput from 'ink-text-input';
3:import { useState } from 'react';
4:import { useSafeNavigate } from '../routes.js';
6:type DatabricksFormState = {
7:  currentStep:
8:    | 'collecting-host'
9:    | 'collecting-api-key'
10:    | 'submitting'
11:    | 'success';
12:  data: {
13:    host: string;
14:    apiKey: string;
15:  };
16:  errors: {
17:    host?: string;
18:    apiKey?: string;
19:  };
20:  isSubmitting: boolean;
21:};
23:const initialState: DatabricksFormState = {
24:  currentStep: 'collecting-host',
25:  data: {
26:    host: '',
27:    apiKey: '',
28:  },
29:  errors: {},
30:  isSubmitting: false,
31:};
33:export function AppDatabricksScreen() {
34:  const [state, setState] = useState<DatabricksFormState>(initialState);
35:  const { safeNavigate } = useSafeNavigate();
37:  const validateHost = (host: string): string | undefined => {
38:    const trimmed = host.trim();
39:    if (!trimmed) return 'Host is required';
40:    if (!URL.canParse(trimmed)) return 'Please enter a valid URL';
41:    return undefined;
42:  };
44:  const validateApiKey = (apiKey: string): string | undefined => {
45:    const trimmed = apiKey.trim();
46:    if (!trimmed) return 'API key is required';
47:    return undefined;
48:  };
50:  const handleHostChange = (value: string) => {
51:    setState((prev) => ({
52:      ...prev,
53:      data: { ...prev.data, host: value },
54:      errors: { ...prev.errors, host: undefined },
55:    }));
56:  };

