import {
  Input,
  Label,
  Button,
  Tooltip,
  TooltipProvider,
  TooltipTrigger,
  TooltipContent,
} from '@appdotbuild/design';
import { Link2, Key, Info, Globe, Shield, CheckCircle2, Loader2 } from 'lucide-react';
import { cn } from '@appdotbuild/design';
import { useFormContext } from 'react-hook-form';
import { useState } from 'react';

type DatabricksConfigFormProps = {
  className?: string;
};

export function DatabricksConfigForm({ className }: DatabricksConfigFormProps) {
  const { register, watch } = useFormContext();
  const [isValidating, setIsValidating] = useState(false);
  const [validationStatus, setValidationStatus] = useState<'idle' | 'success' | 'error'>('idle');

  const hostUrl = watch('databricksConfig.hostUrl');
  const personalAccessToken = watch('databricksConfig.personalAccessToken');

  const canValidate = hostUrl && personalAccessToken;

  const handleValidation = async () => {
    if (!canValidate) return;

    setIsValidating(true);
    setValidationStatus('idle');

    try {
      // Simulate validation - in real implementation, this would test the connection
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Basic validation check
      if (hostUrl.includes('databricks.com') && personalAccessToken.startsWith('dapi')) {
        setValidationStatus('success');
      } else {
        setValidationStatus('error');
      }
    } catch {
      setValidationStatus('error');
    } finally {
      setIsValidating(false);
    }
  };

  return (
    <div className={cn('space-y-6', className)}>
      <div className="space-y-3">
        <Label
          htmlFor="workspace-url"
          className="text-sm font-medium flex items-center gap-2"
        >
          <Link2 className="w-4 h-4" />
          Workspace URL
          <TooltipProvider>
            <Tooltip>
              <TooltipTrigger asChild>
                <Info className="w-3 h-3 text-muted-foreground cursor-help" />
              </TooltipTrigger>
              <TooltipContent className="max-w-xs">
                <p className="text-sm">
                  Find this in your Databricks workspace URL bar. It typically
                  follows the format:
                  https://[workspace-name].cloud.databricks.com
                </p>
              </TooltipContent>
            </Tooltip>
          </TooltipProvider>
        </Label>

        <div className="relative">
          <Input
            id="workspace-url"
            type="url"
            placeholder="https://workspace.cloud.databricks.com"
            className="pl-10 transition-all duration-200"
            {...register('databricksConfig.hostUrl')}
          />
          <Globe className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
        </div>

        <div className="rounded-lg bg-muted/50 p-3 text-xs text-muted-foreground">
          <div className="flex items-start gap-2">
            <Info className="w-3 h-3 mt-0.5 flex-shrink-0" />
            <div>
              <p className="font-medium mb-1">
                How to find your workspace URL:
              </p>
              <ol className="list-decimal list-inside space-y-1">
                <li>Go to your Databricks workspace</li>
                <li>Copy the URL from your browser's address bar</li>
                <li>Remove any path after .com (keep only the domain)</li>
              </ol>
            </div>
          </div>
        </div>
      </div>

      <div className="space-y-3">
        <Label
          htmlFor="access-token"
          className="text-sm font-medium flex items-center gap-2"
        >
          <Key className="w-4 h-4" />
          Personal Access Token
          <TooltipProvider>
            <Tooltip>
              <TooltipTrigger asChild>
                <Info className="w-3 h-3 text-muted-foreground cursor-help" />
              </TooltipTrigger>
              <TooltipContent className="max-w-xs">
                <p className="text-sm">
                  Create a personal access token in your Databricks workspace:
                  User Settings â†’ Developer â†’ Access Tokens
                </p>
              </TooltipContent>
            </Tooltip>
          </TooltipProvider>
        </Label>

        <div className="relative">
          <Input
            id="access-token"
            type="password"
            placeholder="dapiâ€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
            className="pl-10 font-mono text-sm tracking-wider"
            {...register('databricksConfig.personalAccessToken')}
          />
          <Key className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
        </div>

        <div className="rounded-lg bg-muted/50 p-3 text-xs text-muted-foreground">
          <div className="flex items-start gap-2">
            <Shield className="w-3 h-3 mt-0.5 flex-shrink-0" />
            <div>
              <p className="font-medium mb-1">How to create an access token:</p>
              <ol className="list-decimal list-inside space-y-1">
                <li>In Databricks, click your profile â†’ User Settings</li>
                <li>Go to Developer â†’ Access Tokens</li>
                <li>Click "Generate New Token"</li>
                <li>Set expiration and copy the token immediately</li>
              </ol>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
