// @ts-strict
import { Box, Static, Text } from 'ink';
import { useEffect } from 'react';
import { authenticate, ensureIsPrivilegedUser } from './auth/auth';
import { useAuth } from './auth/use-auth';
import { Banner } from './components/ui/Banner';
import { useAnalytics } from './hooks/use-analytics';

function WebLinkButton() {
  return (
    <Box>
      <Text dimColor>Please use </Text>
      <Text underline color="blueBright">
        https://app.build
      </Text>
      <Text dimColor> (web version) instead for all future projects.</Text>
    </Box>
  );
}

// refresh the app every 100ms
const useKeepAlive = () =>
  useEffect(() => {
    setInterval(() => {}, 100);
  }, []);

export const App = () => {
  useKeepAlive();

  return <DeprecationBanner />;

  // return (
  //   <QueryClientProvider client={queryClient}>
  //     <AuthWrapper>
  //       <Box display="flex" width="100%">
  //         <Box flexGrow={1} flexDirection="column">
  //           <AppRouter />
  //         </Box>
  //         <DebugPanel />
  //       </Box>
  //     </AuthWrapper>
  //   </QueryClientProvider>
  // );
};

function AuthWrapper({ children }: { children: React.ReactNode }) {
  const { data, error, isLoading } = useAuth();
  const { trackEvent } = useAnalytics();
  const isAuthenticated = !isLoading && !!data?.isLoggedIn;

  useEffect(() => {
    if (!isAuthenticated) {
      void authenticate();
    } else {
      // ensure the user is a neon employee
      void ensureIsPrivilegedUser();

      // track identify event when user is authenticated
      trackEvent({
        eventType: 'identify',
      });
    }
  }, [isAuthenticated, trackEvent]);

  let content = null;

  if (error) {
    content = <Text color="red">Error: {error.message}</Text>;
  } else if (!data?.isLoggedIn) {
    content = null;
  } else {
    content = children;
  }

  if (!isAuthenticated) {
    return null;
  }

  return (
    <Box flexDirection="column" gap={1}>
      <Static items={['banner']}>
        {() => (
          <Banner key="banner" title="âš ï¸  DEPRECATION NOTICE" borderColor="red">
            <Box flexDirection="column">
              <Text color="red" bold>
                This CLI will be{' '}
                <Text color="red" bold inverse>
                  DEPRECATED
                </Text>{' '}
                soon!
              </Text>
              <WebLinkButton />
            </Box>
          </Banner>
        )}
      </Static>
      {content}
    </Box>
  );
}

function DeprecationBanner() {
  return (
    <Banner key="banner" title="âš ï¸  DEPRECATION NOTICE" borderColor="red">
      <Box flexDirection="column">
        <Text color="red" bold>
          This CLI will be{' '}
          <Text color="red" bold inverse>
            DEPRECATED
          </Text>{' '}
          soon!
        </Text>
        <WebLinkButton />
      </Box>
    </Banner>
  );
}

import { render } from 'ink';
import meow from 'meow';
import { App } from './app.js';
import { useTerminalState } from './hooks/use-terminal-state.js';
import {
  type Environment,
  useEnvironmentStore,
} from './store/environment-store.js';
import { useAnalyticsStore } from './store/analytics-store.js';
import { TemplateMap, useFlagsStore } from './store/flags-store.js';

// in the CLI, node_env is only production or development
const defaultEnv = process.env.NODE_ENV ?? 'development';

const cli = meow(
  `
	Usage
	  $ npx @app.build/cli

	Options
	  --env, -e Agent and platform environment (staging|production) (optional) [default: ${defaultEnv}]
	  --databricks deploy to databricks
    --template, -t Template to use for app creation (trpc_agent|nicegui_agent) [default: trpc_agent]

	Examples
	  $ npx @app.build/cli
	  $ npx @app.build/cli --agent-env staging
	  $ npx @app.build/cli --databricks
	  $ npx @app.build/cli --template nicegui_agent
`,
  {
    importMeta: import.meta,
    flags: {
      env: {
        type: 'string',
        shortFlag: 'a',
        default: defaultEnv,
        choices: ['staging', 'production', 'development'],
      },
      analytics: {
        type: 'boolean',
        default: true,
        description: 'Manage analytics. Example: --analytics false',
      },
      databricks: {
        type: 'boolean',
        default: false,
        description: 'Enable Databricks app creation mode',
      },
      template: {
        type: 'string',
        shortFlag: 't',
        default: 'trpc-react',
        choices: ['python', 'trpc-react', 'laravel'],
      },
    },
  },
);

const { clearTerminal } = useTerminalState();
clearTerminal();

// Set the environment for the agent
useEnvironmentStore.getState().setEnvironment(cli.flags.env as Environment);

// Set analytics preference
useAnalyticsStore.getState().setAnalyticsEnabled(cli.flags.analytics);

// set databricks mode
useFlagsStore.getState().setDatabricksMode(cli.flags.databricks);

// set template id
useFlagsStore.getState().setTemplateId(cli.flags.template as keyof TemplateMap);

render(<App />);

