1:import { Admin } from '@/components/admin';
2:import { dataProvider } from '@/lib/react-admin/data-provider';
3:import { Resource } from 'ra-core';
4:import { createBrowserRouter, RouterProvider, useLocation } from 'react-router';
5:import { StackHandler, StackProvider, StackTheme } from '@stackframe/react';
6:import { stackClientApp } from '@/stack';
7:import { authProvider } from '@/lib/react-admin/auth-provider';
8:import { lazy } from 'react';
9:import { AppWindow, Users } from 'lucide-react';
10:
11:function HandlerRoutes() {
12:  const location = useLocation();
13:
14:  return (
15:    <StackHandler app={stackClientApp} location={location.pathname} fullPage />
16:  );
17:}
18:
19:const UserList = lazy(() => import('@/components/users-list'));
20:const AppList = lazy(() => import('@/components/apps/apps-list'));
21:const AppShow = lazy(() => import('@/components/apps/app-details'));
22:
23:export const App = () => {
24:  const router = createBrowserRouter([
25:    {
26:      path: '/handler/*',
27:      element: <HandlerRoutes />,
28:    },
29:    {
30:      path: '*',
31:      element: (
32:        <Admin dataProvider={dataProvider} authProvider={authProvider}>
33:          <Resource
34:            icon={AppWindow}
35:            name="apps"
36:            list={AppList}
37:            show={AppShow}
38:          />
39:          <Resource icon={Users} name="users" list={UserList} />
40:        </Admin>
41:      ),
42:    },
43:  ]);
44:  return (
45:    <StackProvider app={stackClientApp}>
46:      <StackTheme>
47:        <RouterProvider router={router} />
48:      </StackTheme>
49:    </StackProvider>
50:  );
51:};

