1:import { Trash } from 'lucide-react';
2:import { Button } from '@appdotbuild/design';
3:import {
4:  useDelete,
5:  useNotify,
6:  useRecordContext,
7:  useRedirect,
8:  useResourceContext,
9:  useTranslate,
10:  Translate,
11:} from 'ra-core';
12:
13:export const DeleteButton = ({
14:  size,
15:}: {
16:  size?: 'default' | 'sm' | 'lg' | 'icon';
17:}) => {
18:  const resource = useResourceContext();
19:  const [deleteOne, { isPending }] = useDelete();
20:  const record = useRecordContext();
21:  const notify = useNotify();
22:  const redirect = useRedirect();
23:  const translate = useTranslate();
24:  const handleClick = (e: React.MouseEvent) => {
25:    stopPropagation(e);
26:    if (!record) return;
27:    void deleteOne(
28:      resource,
29:      { id: record.id },
30:      {
31:        mutationMode: 'undoable',
32:        onSuccess: () => {
33:          notify(`resources.${resource}.notifications.deleted`, {
34:            type: 'info',
35:            messageArgs: {
36:              smart_count: 1,
37:              _: translate('ra.notification.deleted', {
38:                smart_count: 1,
39:                _: 'Element deleted',
40:              }),
41:            },
42:            undoable: true,
43:          });
44:          redirect('list', resource);
45:        },
46:      },
47:    );
48:  };
49:  return (
50:    <Button
51:      variant="destructive"
52:      type="button"
53:      onClick={handleClick}
54:      disabled={isPending}
55:      size={size}
56:    >
57:      <Trash />
58:      <Translate i18nKey="ra.action.delete">Delete</Translate>
59:    </Button>
60:  );
61:};
62:
63:// useful to prevent click bubbling in a datagrid with rowClick
64:
65:const stopPropagation = (e: any) => e.stopPropagation();

