1:import { Trash } from 'lucide-react';
2:import { Button } from '@appdotbuild/design';
3:import {
4:  useResourceContext,
5:  useDeleteMany,
6:  useListContext,
7:  useNotify,
8:  useTranslate,
9:  Translate,
10:} from 'ra-core';
11:
12:export const BulkDeleteButton = () => {
13:  const resource = useResourceContext();
14:  const [deleteMany, { isPending }] = useDeleteMany();
15:  const { selectedIds, onUnselectItems } = useListContext();
16:  const notify = useNotify();
17:  const translate = useTranslate();
18:  const handleClick = (e: React.MouseEvent) => {
19:    stopPropagation(e);
20:    void deleteMany(
21:      resource,
22:      { ids: selectedIds },
23:      {
24:        mutationMode: 'undoable',
25:        onSuccess: () => {
26:          onUnselectItems();
27:          notify(`resources.${resource}.notifications.deleted`, {
28:            messageArgs: {
29:              smart_count: selectedIds.length,
30:              _: translate('ra.notification.deleted', {
31:                smart_count: selectedIds.length,
32:                _: `${selectedIds.length} elements deleted`,
33:              }),
34:            },
35:            undoable: true,
36:          });
37:        },
38:      },
39:    );
40:  };
41:  return (
42:    <Button
43:      variant="destructive"
44:      type="button"
45:      onClick={handleClick}
46:      disabled={isPending}
47:    >
48:      <Trash />
49:      <Translate i18nKey="ra.action.delete">Delete</Translate>
50:    </Button>
51:  );
52:};
53:
54:// useful to prevent click bubbling in a datagrid with rowClick
55:
56:const stopPropagation = (e: any) => e.stopPropagation();

