49:// Skeleton loading component that adapts to the table structure
50:function DataTableSkeleton({
51:  children,
52:  skeletonRows = 10,
53:  className,
54:}: {
55:  children: ReactNode;
56:  skeletonRows?: number;
57:  className?: string;
58:}) {
59:  // Count the number of columns by rendering children in header context
60:  const columnCount = useMemo(() => {
61:    let count = 0;
62:
63:    // This is a bit of a hack, but we need to count the columns
64:    // We'll use React.Children.count as a simple approach
65:    const childArray = Array.isArray(children) ? children : [children];
66:    count = childArray.length;
67:
68:    return count;
69:  }, [children]);
70:
71:  return (
72:    <div className={cn('rounded-md border', className)}>
73:      <Table>
74:        <TableHeader>
75:          <TableRow>
76:            {Array.from({ length: columnCount }).map((_, index) => (
77:              <TableHead key={index} className="p-4 text-left font-medium">
78:                <Skeleton className="h-4 w-20" />
79:              </TableHead>
80:            ))}

