1:import type { ReactNode } from 'react';
2:import { useListContext, Translate } from 'ra-core';
3:import { Card } from '@appdotbuild/design';
4:import { Button } from '@appdotbuild/design';
5:import { BulkDeleteButton } from '@/components/admin/bulk-delete-button';
6:import { X } from 'lucide-react';
7:
8:export const BulkActionsToolbar = ({
9:  children = <BulkDeleteButton />,
10:}: {
11:  children?: ReactNode;
12:}) => {
13:  const { selectedIds, onUnselectItems } = useListContext();
14:  if (!selectedIds?.length) {
15:    return null;
16:  }
17:  const handleUnselectAll = (e: React.MouseEvent) => {
18:    e.stopPropagation();
19:    onUnselectItems();
20:  };
21:  return (
22:    <Card className="flex flex-col gap-2 md:gap-6 md:flex-row items-stretch sm:items-center p-2 px-4 w-[90%] sm:w-fit mx-auto sticky bottom-10 z-10 bg-zinc-100 dark:bg-zinc-900">
23:      <div className="flex items-center gap-2">
24:        <Button
25:          variant="ghost"
26:          className="has-[>svg]:px-0"
27:          onClick={handleUnselectAll}
28:        >
29:          <X />
30:        </Button>
31:        <span className="text-sm text-muted-foreground">
32:          <Translate
33:            i18nKey="ra.action.bulk_actions"
34:            options={{ smart_count: selectedIds.length }}
35:          >
36:            {selectedIds.length} rows selected
37:          </Translate>
38:        </span>
39:      </div>
40:      {children}
41:    </Card>
42:  );
43:};

