1:import { createElement, useCallback } from 'react';
2:import {
3:  useCreatePath,
4:  useGetResourceLabel,
5:  useHasDashboard,
6:  useResourceDefinitions,
7:  useTranslate,
8:  useStoreContext,
9:} from 'ra-core';
10:import { Link, useMatch, useNavigate } from 'react-router';
11:import {
12:  Sidebar,
13:  SidebarContent,
14:  SidebarFooter,
15:  SidebarGroup,
16:  SidebarGroupContent,
17:  SidebarHeader,
18:  SidebarMenu,
19:  SidebarMenuButton,
20:  SidebarMenuItem,
21:  useSidebar,
22:} from '@appdotbuild/design';
23:import { List, House } from 'lucide-react';
24:
25:export function AppSidebar() {
26:  const hasDashboard = useHasDashboard();
27:  const resources = useResourceDefinitions();
28:  const { openMobile, setOpenMobile } = useSidebar();
29:  const handleClick = () => {
30:    if (openMobile) {
31:      setOpenMobile(false);
32:    }
33:  };
34:  return (
35:    <Sidebar variant="floating" collapsible="icon">
36:      <SidebarHeader>
37:        <SidebarMenu>
38:          <SidebarMenuItem>
39:            <SidebarMenuButton
40:              asChild
41:              className="data-[slot=sidebar-menu-button]:!p-1.5"
42:            >
43:              <Link to="/">
44:                <img src="/app-icon.svg" alt="app.build" className="!size-5" />
45:                <span className="text-base font-semibold">app.build Admin</span>
46:              </Link>
47:            </SidebarMenuButton>
48:          </SidebarMenuItem>
49:        </SidebarMenu>
50:      </SidebarHeader>
51:      <SidebarContent>
52:        <SidebarGroup>
53:          <SidebarGroupContent>
54:            <SidebarMenu>
55:              {hasDashboard ? (
56:                <DashboardMenuItem onClick={handleClick} />
57:              ) : null}
58:              {Object.keys(resources)
59:                .filter((name) => resources[name]?.hasList)
60:                .map((name) => (
61:                  <ResourceMenuItem
62:                    key={name}
63:                    name={name}
64:                    onClick={handleClick}
65:                  />
66:                ))}
67:            </SidebarMenu>
68:          </SidebarGroupContent>
69:        </SidebarGroup>
70:      </SidebarContent>
71:      <SidebarFooter />
72:    </Sidebar>
73:  );
74:}
75:
76:export const DashboardMenuItem = ({ onClick }: { onClick?: () => void }) => {
77:  const translate = useTranslate();
78:  const label = translate('ra.page.dashboard', {
79:    _: 'Dashboard',
80:  });
81:  const match = useMatch({ path: '/', end: true });
82:  return (
83:    <SidebarMenuItem>
84:      <SidebarMenuButton asChild isActive={!!match}>
85:        <Link to="/" onClick={onClick}>
86:          <House />
87:          {label}
88:        </Link>
89:      </SidebarMenuButton>
90:    </SidebarMenuItem>
91:  );
92:};
93:
94:export const ResourceMenuItem = ({
95:  name,
96:  onClick,
97:}: {
98:  name: string;
99:  onClick?: () => void;
100:}) => {
101:  const resources = useResourceDefinitions();
102:  const getResourceLabel = useGetResourceLabel();
103:  const createPath = useCreatePath();
104:  const navigate = useNavigate();
105:  // Try to get list context, but it may not be available in all contexts
106:  const storeContext = useStoreContext();
107:
108:  const to = createPath({
109:    resource: name,
110:    type: 'list',
111:  });
112:  const match = useMatch({ path: to, end: false });
113:
114:  const handleClick = useCallback(
115:    (e: React.MouseEvent) => {
116:      e.preventDefault();
117:
118:      // Clear filters if we're in a list context
119:      if (storeContext?.reset) {
120:        storeContext.reset();
121:      }
122:
123:      // Navigate after clearing filters
124:      void navigate(to, { state: { _scrollToTop: true } });
125:
126:      // Call the original onClick handler for mobile sidebar
127:      if (onClick) {
128:        onClick();
129:      }
130:    },
131:    [storeContext, navigate, to, onClick],
132:  );
133:
134:  if (!resources || !resources[name]) return null;
135:
136:  return (
137:    <SidebarMenuItem>
138:      <SidebarMenuButton asChild isActive={!!match}>
139:        <Link onClick={handleClick} to={to} state={{ _scrollToTop: true }}>
140:          {resources[name].icon ? (
141:            createElement(resources[name].icon)
142:          ) : (
143:            <List />
144:          )}
145:          {getResourceLabel(name, 2)}
146:        </Link>
147:      </SidebarMenuButton>
148:    </SidebarMenuItem>
149:  );
150:};

