91:  );
92:};
93:
94:export const ResourceMenuItem = ({
95:  name,
96:  onClick,
97:}: {
98:  name: string;
99:  onClick?: () => void;
100:}) => {
101:  const resources = useResourceDefinitions();
102:  const getResourceLabel = useGetResourceLabel();
103:  const createPath = useCreatePath();
104:  const navigate = useNavigate();
105:  // Try to get list context, but it may not be available in all contexts
106:  const storeContext = useStoreContext();
107:
108:  const to = createPath({
109:    resource: name,
110:    type: 'list',
111:  });
112:  const match = useMatch({ path: to, end: false });
113:
114:  const handleClick = useCallback(
115:    (e: React.MouseEvent) => {
116:      e.preventDefault();
117:
118:      // Clear filters if we're in a list context
119:      if (storeContext?.reset) {
120:        storeContext.reset();
121:      }
122:
123:      // Navigate after clearing filters
124:      void navigate(to, { state: { _scrollToTop: true } });
125:
126:      // Call the original onClick handler for mobile sidebar
127:      if (onClick) {
128:        onClick();
129:      }
130:    },
131:    [storeContext, navigate, to, onClick],
132:  );
133:
134:  if (!resources || !resources[name]) return null;
135:
136:  return (
137:    <SidebarMenuItem>
138:      <SidebarMenuButton asChild isActive={!!match}>
139:        <Link onClick={handleClick} to={to} state={{ _scrollToTop: true }}>
140:          {resources[name].icon ? (
141:            createElement(resources[name].icon)
142:          ) : (
143:            <List />
144:          )}
145:          {getResourceLabel(name, 2)}
146:        </Link>
147:      </SidebarMenuButton>
148:    </SidebarMenuItem>
149:  );
150:};

