1:import type { Paginated } from '@appdotbuild/core';
2:import {
3:  asc,
4:  desc,
5:  getTableColumns,
6:  ilike,
7:  or,
8:  sql,
9:  type SQLWrapper,
10:  eq,
11:  and,
12:  SQL,
13:} from 'drizzle-orm';
14:import { usersSync as users } from 'drizzle-orm/neon';
15:import type { FastifyReply, FastifyRequest } from 'fastify';
16:import { apps, db, customMessageLimits } from '../../db';
17:
18:type User = typeof users.$inferSelect;
19:
20:export async function listUsersForAdmin(
21:  request: FastifyRequest,
22:  reply: FastifyReply,
23:): Promise<
24:  Paginated<User & { appsCount?: number; dailyMessageLimit?: number }>
25:> {
26:  const {
27:    limit = 10,
28:    page = 1,
29:    sort = 'createdAt',
30:    order = 'desc',
31:    search = '',
32:  } = request.query as {
33:    limit?: number;
34:    page?: number;
35:    sort?: string;
36:    order?: string;
37:    search?: string;
38:  };
39:
40:  if (limit > 100) {
41:    return reply.status(400).send({
42:      error: 'Limit cannot exceed 100',
43:    });
44:  }
45:
46:  const pagesize = Math.min(Math.max(1, Number(limit)), 100);
47:  const pageNum = Math.max(1, Number(page));
48:  const offset = (pageNum - 1) * pagesize;
49:
50:  // Handle custom sorting for apps count
51:  let orderBy: SQL;
52:  if (sort === 'appsCount') {
53:    // For apps count sorting, use the same $count method
54:    const appsCountExpression = db.$count(apps, eq(apps.ownerId, users.id));
55:
56:    orderBy =
57:      order.toUpperCase() === 'ASC'
58:        ? asc(appsCountExpression)
59:        : desc(appsCountExpression);
60:  } else {
61:    const sortBy = users[sort as keyof typeof users] as SQLWrapper;
62:    orderBy = order.toUpperCase() === 'ASC' ? asc(sortBy) : desc(sortBy);
63:  }
64:
65:  const { ...columns } = getTableColumns(users);
66:
67:  // Build search conditions
68:  let searchConditions = undefined;
69:  if (search.trim()) {
70:    const searchTerm = `%${search.trim()}%`;
71:    searchConditions = or(
72:      ilike(users.id, searchTerm),
73:      ilike(users.email, searchTerm),
74:      ilike(users.name, searchTerm),
75:    );
76:  }
77:
78:  const filterConditions = [searchConditions];
79:
80:  const countQuery = db
81:    .select({ count: sql`count(*)` })
82:    .from(users)
83:    .where(and(...filterConditions));
84:
85:  const DEFAULT_MESSAGE_LIMIT = Number(process.env.DAILY_MESSAGE_LIMIT) || 10;
86:
87:  const usersQuery = db
88:    .select({
89:      ...columns,
90:      appsCount: db.$count(apps, eq(apps.ownerId, users.id)),
91:      dailyMessageLimit:
92:        sql<number>`COALESCE(${customMessageLimits.dailyLimit}, ${DEFAULT_MESSAGE_LIMIT})`.as(
93:          'dailyMessageLimit',
94:        ),
95:    })
96:    .from(users)
97:    .leftJoin(customMessageLimits, eq(users.id, customMessageLimits.userId))
98:    .orderBy(orderBy)
99:    .limit(pagesize)
100:    .offset(offset)
101:    .where(and(...filterConditions));
102:
103:  const [countResult, usersList] = await Promise.all([countQuery, usersQuery]);
104:  const totalCount = Number(countResult[0]?.count || 0);
105:
106:  return {
107:    data: usersList,
108:    pagination: {
109:      total: totalCount,
110:      page: pageNum,
111:      limit: pagesize,
112:      totalPages: Math.ceil(totalCount / pagesize),
113:    },
114:  };
115:}
116:
117:export async function updateUserForAdmin(
118:  request: FastifyRequest,
119:  reply: FastifyReply,
120:) {
121:  const MAX_DAILY_MESSAGE_LIMIT = 1000;
122:  const { id: userId } = request.params as { id: string };
123:  const { dailyMessageLimit } = request.body as { dailyMessageLimit?: number };
124:
125:  // Validate input - only allow dailyMessageLimit updates
126:  if (dailyMessageLimit === undefined) {
127:    return reply.status(400).send({
128:      error: 'dailyMessageLimit is required',
129:    });
130:  }
131:
132:  if (!Number.isInteger(dailyMessageLimit) || dailyMessageLimit < 1) {
133:    return reply.status(400).send({
134:      error: 'dailyMessageLimit must be a positive integer',
135:    });
136:  }
137:
138:  if (dailyMessageLimit > MAX_DAILY_MESSAGE_LIMIT) {
139:    return reply.status(400).send({
140:      error: 'dailyMessageLimit must be less than 10,000',
141:    });
142:  }
143:
144:  // Check if user exists
145:  const existingUser = await db
146:    .select()
147:    .from(users)
148:    .where(eq(users.id, userId))
149:    .limit(1);
150:
151:  if (existingUser.length === 0) {
152:    return reply.status(404).send({
153:      error: 'User not found',
154:    });
155:  }
156:
157:  const user = existingUser[0];
158:
159:  // Upsert custom message limit
160:  await db
161:    .insert(customMessageLimits)
162:    .values({
163:      userId,
164:      dailyLimit: dailyMessageLimit,
165:      updatedAt: new Date(),
166:    })
167:    .onConflictDoUpdate({
168:      target: customMessageLimits.userId,
169:      set: {
170:        dailyLimit: dailyMessageLimit,
171:        updatedAt: new Date(),
172:      },
173:    });
174:
175:  return reply.send({
176:    ...user,
177:    dailyMessageLimit,
178:  });
179:}

