47:export async function listAllAppsForAdmin(
48:  request: FastifyRequest,
49:  reply: FastifyReply,
50:): Promise<Paginated<Omit<App, 'agentState'>>> {
51:  const {
52:    limit = 10,
53:    page = 1,
54:    sort = 'createdAt',
55:    order = 'desc',
56:    search = '',
57:    ownerId,
58:    appStatus,
59:  } = request.query as {
60:    limit?: number;
61:    page?: number;
62:    sort?: string;
63:    order?: string;
64:    search?: string;
65:    ownerId?: string;
66:    appStatus?: string;
67:  };
68:
69:  if (limit > 100) {
70:    return reply.status(400).send({
71:      error: 'Limit cannot exceed 100',
72:    });
73:  }
74:
75:  const pagesize = Math.min(Math.max(1, Number(limit)), 100);
76:  const pageNum = Math.max(1, Number(page));
77:  const offset = (pageNum - 1) * pagesize;
78:  const sortBy = apps[sort as keyof typeof apps] as SQLWrapper;
79:  const orderBy = order.toUpperCase() === 'ASC' ? asc(sortBy) : desc(sortBy);
80:
81:  const { agentState, ...columns } = getTableColumns(apps);
82:
83:  // Build search conditions
84:  let searchConditions = undefined;
85:  if (search.trim()) {
86:    const searchTerm = `%${search.trim()}%`;
87:    searchConditions = or(
88:      ilike(apps.ownerId, searchTerm),
89:      ilike(apps.name, searchTerm),
90:      ilike(apps.traceId, searchTerm),
91:    );
92:  }
93:
94:  // Build owner filter conditions
95:  let ownerConditions = undefined;
96:  if (ownerId) {
97:    ownerConditions = eq(apps.ownerId, ownerId);
98:  }
99:
100:  // Build deletion status conditions based on appStatus
101:  let appStatusConditions = undefined;
102:  if (appStatus === 'active') {
103:    // Only show active apps (not deleted)
104:    appStatusConditions = sql`${apps.deletedAt} IS NULL`;
105:  } else if (appStatus === 'deleted') {
106:    // Only show deleted apps
107:    appStatusConditions = sql`${apps.deletedAt} IS NOT NULL`;
108:  } else if (appStatus === 'all') {
109:    // Show all apps (both deleted and active) - no filter needed
110:    appStatusConditions = undefined;
111:  } else {
112:    // Default: show all apps (both active and deleted)
113:    appStatusConditions = undefined;
114:  }
115:
116:  const filterConditions = [
117:    searchConditions,
118:    ownerConditions,
119:    appStatusConditions,
120:  ].filter(Boolean);
121:
122:  const countQuery = db
123:    .select({ count: sql`count(*)` })
124:    .from(apps)
125:    .where(and(...filterConditions));
126:
127:  const appsP = db
128:    .select(columns)
129:    .from(apps)
130:    .orderBy(orderBy)
131:    .limit(pagesize)
132:    .offset(offset)
133:    .where(and(...filterConditions));
134:
135:  const [countResult, appsList] = await Promise.all([countQuery, appsP]);
136:  const totalCount = Number(countResult[0]?.count || 0);
137:
138:  return {
139:    data: appsList,
140:    pagination: {
141:      total: totalCount,
142:      page: pageNum,
143:      limit: pagesize,
144:      totalPages: Math.ceil(totalCount / pagesize),
145:    },
146:  };
147:}

