2:import { eq } from 'drizzle-orm';
3:import { apps, db } from '../../db';
4:import { getAgentSnapshotFolders } from '../../s3/get-agent-snapshot-folders';
5:import { getAppSingleIterationJson } from '../../s3/get-single-iteration-json';
6:import type {
7:  AgentSnapshotMetadata,
8:  AgentSnapshotIterationJsonData,
9:  AgentSnapshotFolder,
10:} from '@appdotbuild/core';
11:import { getAgentSnapshotMetadata } from '../../s3/get-agent-snapshots-metadata';
12:
13:/**
14: * Get all log folders for an app
15: * @param request - Fastify request
16: * @param reply - Fastify reply
17: * @returns Log folders
18: */
19:export async function getAppLogFolders(
20:  request: FastifyRequest,
21:  reply: FastifyReply,
22:): Promise<AgentSnapshotFolder[]> {
23:  const { id } = request.params as { id: string };
24:
25:  if (!id) {
26:    return reply.status(400).send({
27:      error: 'App ID is required',
28:    });
29:  }
30:
31:  try {
32:    // Verify app exists
33:    const appResult = await db
34:      .select({ id: apps.id })
35:      .from(apps)
36:      .where(eq(apps.id, id))
37:      .limit(1);
38:
39:    if (!appResult || !appResult[0]) {
40:      return reply.status(404).send({
41:        error: 'App not found',
42:      });
43:    }
44:
45:    // Get log folders from S3
46:    const folders = await getAgentSnapshotFolders(id);
47:    return folders;
48:  } catch (error) {
49:    console.error(`Error getting log folders for app ${id}:`, error);
50:    return reply.status(500).send({
51:      error: 'Failed to retrieve log folders',
52:      details: error instanceof Error ? error.message : 'Unknown error',
53:    });
54:  }
55:}
56:
57:/**
58: * Get metadata for a agent snapshot
59: * @param request - Fastify request
60: * @param reply - Fastify reply
61: * @returns Agent snapshot metadata
62: */
63:export async function getAppAgentSnapshotMetadata(
64:  request: FastifyRequest,
65:  reply: FastifyReply,
66:): Promise<AgentSnapshotMetadata> {
67:  const { id, traceId } = request.params as { id: string; traceId: string };
68:
69:  if (!id || !traceId) {
70:    return reply.status(400).send({
71:      error: 'App ID and trace ID are required',
72:    });
73:  }
74:
75:  try {
76:    const metadata = await getAgentSnapshotMetadata(id, traceId);
77:    return metadata;
78:  } catch (error) {
79:    console.error(
80:      `Error getting metadata for app ${id}, trace ${traceId}:`,
81:      error,
82:    );
83:    return reply.status(500).send({
84:      error: 'Failed to retrieve trace metadata',
85:      details: error instanceof Error ? error.message : 'Unknown error',
86:    });
87:  }
88:}
89:
90:/**
91: * Get a single iteration of a agent snapshot
92: * @param request - Fastify request
93: * @param reply - Fastify reply
94: * @returns Agent snapshot iteration data
95: */
96:export async function getAppSingleIterationJsonData(
97:  request: FastifyRequest,
98:  reply: FastifyReply,
99:): Promise<AgentSnapshotIterationJsonData> {
100:  const { id, traceId, iteration } = request.params as {
101:    id: string;
102:    traceId: string;
103:    iteration: string;
104:  };
105:
106:  if (!id || !traceId || !iteration) {
107:    return reply.status(400).send({
108:      error: 'App ID, trace ID, and iteration are required',
109:    });
110:  }
111:
112:  const iterationNum = parseInt(iteration, 10);
113:  if (isNaN(iterationNum) || iterationNum < 1) {
114:    return reply.status(400).send({
115:      error: 'Iteration must be a positive integer',
116:    });
117:  }
118:
119:  try {
120:    // Verify app exists
121:    const appResult = await db
122:      .select({ id: apps.id })
123:      .from(apps)
124:      .where(eq(apps.id, id))
125:      .limit(1);
126:
127:    if (!appResult || !appResult[0]) {
128:      return reply.status(404).send({
129:        error: 'App not found',
130:      });
131:    }
132:
133:    // Security: Extract trace ID without app prefix for validation
134:    const traceIdWithoutPrefix = traceId.replace(`app-${id}.req-`, '');
135:    if (traceIdWithoutPrefix === traceId) {
136:      return reply.status(403).send({
137:        error: 'Invalid trace ID format for this app',
138:      });
139:    }
140:
141:    // Get JSON data for the specific iteration
142:    const iterationData = await getAppSingleIterationJson(
143:      id,
144:      traceIdWithoutPrefix,
145:      iterationNum,
146:    );
147:
148:    if (!iterationData) {
149:      return reply.status(404).send({
150:        error: 'Iteration not found',
151:      });
152:    }
153:
154:    return iterationData;
155:  } catch (error) {
156:    console.error(
157:      `Error getting iteration ${iteration} JSON for app ${id}, trace ${traceId}:`,
158:      error,
159:    );
160:    return reply.status(500).send({
161:      error: 'Failed to retrieve iteration JSON data',
162:      details: error instanceof Error ? error.message : 'Unknown error',
163:    });
164:  }
165:}

