     1â†’import { Trash } from 'lucide-react';
     2â†’import { Button } from '@appdotbuild/design';
     3â†’import {
     4â†’  useDelete,
     5â†’  useNotify,
     6â†’  useRecordContext,
     7â†’  useRedirect,
     8â†’  useResourceContext,
     9â†’  useTranslate,
    10â†’  Translate,
    11â†’} from 'ra-core';
    12â†’
    13â†’export const DeleteButton = ({
    14â†’  size,
    15â†’}: {
    16â†’  size?: 'default' | 'sm' | 'lg' | 'icon';
    17â†’}) => {
    18â†’  const resource = useResourceContext();
    19â†’  const [deleteOne, { isPending }] = useDelete();
    20â†’  const record = useRecordContext();
    21â†’  const notify = useNotify();
    22â†’  const redirect = useRedirect();
    23â†’  const translate = useTranslate();
    24â†’  const handleClick = (e: React.MouseEvent) => {
    25â†’    stopPropagation(e);
    26â†’    if (!record) return;
    27â†’    void deleteOne(
    28â†’      resource,
    29â†’      { id: record.id },
    30â†’      {
    31â†’        mutationMode: 'undoable',
    32â†’        onSuccess: () => {
    33â†’          notify(`resources.${resource}.notifications.deleted`, {
    34â†’            type: 'info',
    35â†’            messageArgs: {
    36â†’              smart_count: 1,
    37â†’              _: translate('ra.notification.deleted', {
    38â†’                smart_count: 1,
    39â†’                _: 'Element deleted',
    40â†’              }),
    41â†’            },
    42â†’            undoable: true,
    43â†’          });
    44â†’          redirect('list', resource);
    45â†’        },
    46â†’      },
    47â†’    );
    48â†’  };
    49â†’  return (
    50â†’    <Button
    51â†’      variant="destructive"
    52â†’      type="button"
    53â†’      onClick={handleClick}
    54â†’      disabled={isPending}
    55â†’      size={size}
