export async function listAllAppsForAdmin(
  request: FastifyRequest,
  reply: FastifyReply,
): Promise<Paginated<Omit<App, 'agentState'>>> {
  const {
    limit = 10,
    page = 1,
    sort = 'createdAt',
    order = 'desc',
    search = '',
    ownerId,
    showDeleted = 'false',
  } = request.query as {
    limit?: number;
    page?: number;
    sort?: string;
    order?: string;
    search?: string;
    ownerId?: string;
    showDeleted?: string;
  };

  if (limit > 100) {
    return reply.status(400).send({
      error: 'Limit cannot exceed 100',
    });
  }

  const pagesize = Math.min(Math.max(1, Number(limit)), 100);
  const pageNum = Math.max(1, Number(page));
  const offset = (pageNum - 1) * pagesize;
  const sortBy = apps[sort as keyof typeof apps] as SQLWrapper;
  const orderBy = order.toUpperCase() === 'ASC' ? asc(sortBy) : desc(sortBy);

  const { agentState, ...columns } = getTableColumns(apps);

  // Build search conditions
  let searchConditions = undefined;
  if (search.trim()) {
    const searchTerm = `%${search.trim()}%`;
    searchConditions = or(
      ilike(apps.ownerId, searchTerm),
      ilike(apps.name, searchTerm),
      ilike(apps.traceId, searchTerm),
    );
  }

  // Build owner filter conditions
  let ownerConditions = undefined;
  if (ownerId) {
    ownerConditions = eq(apps.ownerId, ownerId);
  }

  // Build deletion status conditions
  let deletionConditions = undefined;
  const shouldShowDeleted = showDeleted === 'true';
  if (!shouldShowDeleted) {
    // Only show active apps (not deleted)
    deletionConditions = sql`${apps.deletedAt} IS NULL`;
  }
  // If showDeleted is true, we include all apps (both deleted and active)

  const filterConditions = [searchConditions, ownerConditions, deletionConditions].filter(Boolean);

  const countQuery = db
    .select({ count: sql`count(*)` })
    .from(apps)
    .where(and(...filterConditions));

  const appsP = db
    .select(columns)
    .from(apps)
    .orderBy(orderBy)
    .limit(pagesize)
    .offset(offset)
    .where(and(...filterConditions));

  const [countResult, appsList] = await Promise.all([countQuery, appsP]);
  const totalCount = Number(countResult[0]?.count || 0);

  return {
    data: appsList,
    pagination: {
      total: totalCount,
      page: pageNum,
      limit: pagesize,
      totalPages: Math.ceil(totalCount / pagesize),
    },
  };
}

