import React from 'react';
import { Button } from '@appdotbuild/design';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@appdotbuild/design';
import {
  useNotify,
  useRecordContext,
  useRefresh,
  useDataProvider,
} from 'ra-core';
import type { ExtendedDataProvider } from '@/lib/react-admin/data-provider';

export function RestoreAppButton() {
  const record = useRecordContext();
  const [isOpen, setIsOpen] = React.useState(false);
  const [isRestoring, setIsRestoring] = React.useState(false);
  const notify = useNotify();
  const refresh = useRefresh();
  const dataProvider = useDataProvider<ExtendedDataProvider>();

  if (!record?.deletedAt) return null;

  const handleRestore = async () => {
    if (!record?.id) return;

    setIsRestoring(true);
    try {
      await dataProvider.restoreApp(record.id);

      notify('App restored successfully', {
        type: 'success',
        messageArgs: { smart_count: 1 },
        undoable: false,
      });

      // Refresh the list to show updated data
      refresh();
      setIsOpen(false);
    } catch (error) {
      console.error('Error restoring app:', error);
      notify(
        error instanceof Error 
          ? `Failed to restore app: ${error.message}`
          : 'Failed to restore app. Please try again.',
        {
          type: 'error',
          messageArgs: { smart_count: 1 },
        }
      );
    } finally {
      setIsRestoring(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button variant="outline" size="sm" className="h-8">
          Restore
        </Button>
      </DialogTrigger>
      <DialogContent>
        <DialogHeader>
          <DialogTitle>Restore App</DialogTitle>
        </DialogHeader>
        <div className="space-y-4">
          <p>Are you sure you want to restore this app? This will make it active again.</p>
          <div className="flex justify-end gap-2">
            <Button
              variant="outline"
              onClick={() => setIsOpen(false)}
              disabled={isRestoring}
            >
              Cancel
            </Button>
            <Button
              variant="default"
              onClick={handleRestore}
              disabled={isRestoring}
            >
              {isRestoring ? 'Restoring...' : 'Restore App'}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

import { Button } from '@appdotbuild/design';
import { useListContext } from 'ra-core';

export function AppStatusFilter() {
  const { filterValues, setFilters } = useListContext();

  // Determine current status based on filter values
  const getCurrentStatus = () => {
    if (filterValues.showDeleted === 'true' && filterValues.appStatus === 'deleted') {
      return 'deleted';
    }
    if (filterValues.showDeleted === 'true' && filterValues.appStatus === 'all') {
      return 'all';
    }
    return 'active';
  };

  const currentStatus = getCurrentStatus();

  const handleStatusChange = (status: 'active' | 'deleted' | 'all') => {
    const newFilters = { ...filterValues };

    // Remove existing status filters
    delete newFilters.showDeleted;
    delete newFilters.appStatus;

    // Apply new filters based on status
    if (status === 'deleted') {
      newFilters.showDeleted = 'true';
      newFilters.appStatus = 'deleted';
    } else if (status === 'all') {
      newFilters.showDeleted = 'true';
      newFilters.appStatus = 'all';
    }
    // For 'active', we don't need any additional filters (default behavior)

    setFilters(newFilters);
  };

  return (
    <div className="flex items-center gap-1 border rounded-md p-1">
      <Button
        variant={currentStatus === 'active' ? 'secondary' : 'ghost'}
        size="sm"
        onClick={() => handleStatusChange('active')}
        className="h-8 text-xs"
      >
        Active
      </Button>
      <Button
        variant={currentStatus === 'deleted' ? 'secondary' : 'ghost'}
        size="sm"
        onClick={() => handleStatusChange('deleted')}
        className="h-8 text-xs"
      >
        Deleted
      </Button>
      <Button
        variant={currentStatus === 'all' ? 'secondary' : 'ghost'}
        size="sm"
        onClick={() => handleStatusChange('all')}
        className="h-8 text-xs"
      >
        All
      </Button>
    </div>
  );
}

