diff --git a/apps/web/src/components/shared/app-link/app-link.tsx b/apps/web/src/components/shared/app-link/app-link.tsx
new file mode 100644
index 0000000..95f80f0
--- /dev/null
+++ b/apps/web/src/components/shared/app-link/app-link.tsx
@@ -0,0 +1,37 @@
+import { cn } from '@design/lib';
+import { Link } from '@tanstack/react-router';
+
+type AppLinkProps = {
+  children: React.ReactNode;
+  to: string;
+  variant?: 'internal' | 'external';
+} & React.ComponentProps<'a'>;
+
+export function AppLink({
+  children,
+  to,
+  variant = 'internal',
+  className,
+  ...props
+}: AppLinkProps) {
+  if (variant === 'external') {
+    return (
+      <a href={to} {...props}>
+        {children}
+      </a>
+    );
+  }
+
+  return (
+    <Link
+      className={cn(
+        'text-sm font-medium transition-colors text-muted-foreground hover:text-secondary-foreground active:text-foreground',
+        className,
+      )}
+      to={to}
+      {...props}
+    >
+      {children}
+    </Link>
+  );
+}

