import { PrismaClient } from '@prisma/client'

export interface Env {
  DATABASE_URL: string
}

// Initialize Prisma client
let prisma: PrismaClient | null = null

function getPrismaClient(databaseUrl: string) {
  if (!prisma) {
    prisma = new PrismaClient({
      datasources: {
        db: {
          url: databaseUrl
        }
      }
    })
  }
  return prisma
}

// CORS headers
const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, OPTIONS',
  'Access-Control-Allow-Headers': 'Content-Type, Authorization'
}

// Helper function to handle CORS
function handleCORS(request: Request) {
  if (request.method === 'OPTIONS') {
    return new Response(null, {
      status: 200,
      headers: corsHeaders
    })
  }
  return null
}

// Helper function to create JSON response with CORS
function jsonResponse(data: any, status = 200) {
  return new Response(JSON.stringify(data), {
    status,
    headers: {
      'Content-Type': 'application/json',
      ...corsHeaders
    }
  })
}

// Email validation
function isValidEmail(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
  return emailRegex.test(email)
}

export default {
  async fetch(request: Request, env: Env): Promise<Response> {
    // Handle CORS preflight
    const corsResponse = handleCORS(request)
    if (corsResponse) return corsResponse

    const url = new URL(request.url)
    const path = url.pathname
    const method = request.method

    try {
      // Initialize Prisma client
      const db = getPrismaClient(env.DATABASE_URL)

      // Health check endpoint
      if (path === '/api/health' && method === 'GET') {
        return jsonResponse({ 
          status: 'healthy', 
          timestamp: new Date().toISOString(),
          service: 'ai-product-newsletter-api'
        })
      }

      // Subscribe endpoint
      if (path === '/api/subscribe' && method === 'POST') {
        try {
          const body = await request.json()
          const { email } = body

          // Validate email
          if (!email || !isValidEmail(email)) {
            return jsonResponse({ 
              error: 'Valid email address is required' 
            }, 400)
          }

          // Check if subscriber already exists
          const existingSubscriber = await db.subscriber.findUnique({
            where: { email: email.toLowerCase() }
          })

          if (existingSubscriber) {
            if (existingSubscriber.status === 'UNSUBSCRIBED') {
              // Reactivate unsubscribed user
              await db.subscriber.update({
                where: { id: existingSubscriber.id },
                data: { 
                  status: 'ACTIVE',
                  updatedAt: new Date()
                }
              })
              return jsonResponse({ 
                message: 'Welcome back! Your subscription has been reactivated.',
                subscriber: { email: existingSubscriber.email }
              })
            } else {
              return jsonResponse({ 
                message: 'You are already subscribed to our newsletter.' 
              }, 409)
            }
          }

          // Create new subscriber
          const subscriber = await db.subscriber.create({
            data: {
              email: email.toLowerCase(),
              status: 'ACTIVE',
              preferences: null
            }
          })

          return jsonResponse({ 
            message: 'Successfully subscribed! Welcome to AI Product Newsletter.',
            subscriber: { email: subscriber.email }
          }, 201)

        } catch (error) {
          console.error('Subscribe error:', error)
          return jsonResponse({ 
            error: 'Failed to process subscription' 
          }, 500)
        }
      }

      // Unsubscribe endpoint
      if (path === '/api/unsubscribe' && method === 'POST') {
        try {
          const body = await request.json()
          const { email } = body

          if (!email || !isValidEmail(email)) {
            return jsonResponse({ 
              error: 'Valid email address is required' 
            }, 400)
          }

          // Find and update subscriber
          const subscriber = await db.subscriber.findUnique({
            where: { email: email.toLowerCase() }
          })

          if (!subscriber) {
            return jsonResponse({ 
              error: 'Email address not found in our database' 
            }, 404)
          }

          if (subscriber.status === 'UNSUBSCRIBED') {
            return jsonResponse({ 
              message: 'You are already unsubscribed.' 
            })
          }

          // Update subscriber status
          await db.subscriber.update({
            where: { id: subscriber.id },
            data: { 
              status: 'UNSUBSCRIBED',
              updatedAt: new Date()
            }
          })

          return jsonResponse({ 
            message: 'Successfully unsubscribed. Sorry to see you go!' 
          })

        } catch (error) {
          console.error('Unsubscribe error:', error)
          return jsonResponse({ 
            error: 'Failed to process unsubscription' 
          }, 500)
        }
      }

      // 404 for unknown routes
      return jsonResponse({ 
        error: 'Not found' 
      }, 404)

    } catch (error) {
      console.error('Request error:', error)
      return jsonResponse({ 
        error: 'Internal server error' 
      }, 500)
    }
  },

  // Scheduled event handler for cron jobs
  async scheduled(event: ScheduledEvent, env: Env): Promise<void> {
    console.log('Cron job triggered:', event.cron)

    try {
      const db = getPrismaClient(env.DATABASE_URL)

      // Get count of active subscribers
      const subscriberCount = await db.subscriber.count({
        where: { status: 'ACTIVE' }
      })

      console.log(`Newsletter generation triggered for ${subscriberCount} active subscribers`)

      // TODO: Implement newsletter generation logic
      // This will be expanded in future phases to include:
      // - AI content aggregation
      // - Newsletter HTML generation
      // - Email sending via Resend/SendGrid
      // - Analytics tracking

    } catch (error) {
      console.error('Scheduled task error:', error)
    }
  }
}

