          }, 500)
        }
      }

      // 404 for unknown routes
      return jsonResponse({ 
        error: 'Not found' 
      }, 404)

    } catch (error) {
      console.error('Request error:', error)
      return jsonResponse({ 
        error: 'Internal server error' 
