    const url = new URL(request.url)
    const path = url.pathname
    const method = request.method

    try {
      // Initialize Prisma client
      const db = getPrismaClient(env.DATABASE_URL)

      // Health check endpoint
      if (path === '/api/health' && method === 'GET') {
