import { handleSubscribe } from './api/subscribe'
import { handleUnsubscribe } from './api/unsubscribe'
import { handleHealth } from './api/health'

// Cron job handlers
import { generateWeeklyNewsletter } from './cron/newsletter-generator'

// Shared utilities
import { getPrismaClient } from './shared/database'
import { handleCORS, jsonResponse } from './shared/cors'

export interface Env {
  DATABASE_URL: string
}

export default {
  async fetch(request: Request, env: Env): Promise<Response> {
    // Handle CORS preflight
